export type BudgetType = 'PERSONAL' | 'SHARED';

export type AccountType =
  | 'CHECKING_ACCOUNT'
  | 'SAVINGS_ACCOUNT'
  | 'PHYSICAL_WALLET'
  | 'DIGITAL_WALLET'
  | 'INVESTMENT_ACCOUNT';

export interface BudgetParticipantDto {
  id: string;
  name: string;
  email: string;
}

export interface BudgetTotalsDto {
  accountsBalance: number;
  monthIncome: number;
  monthExpense: number;
  netMonth: number;
}

export interface AccountDto {
  id: string;
  name: string;
  type: AccountType;
  balance: number;
}

export interface BudgetDto {
  id: string;
  name: string;
  type: BudgetType;
  participantsCount: number;
}

export interface BudgetOverviewDto {
  id: string;
  name: string;
  type: BudgetType;
  participants: BudgetParticipantDto[];
  totals: BudgetTotalsDto;
  accounts: AccountDto[];
}

import { Injectable, signal, computed } from '@angular/core';
import { environment } from '../../../../environments/environment';

export interface ApiConfig {
  baseUrl: string;
  timeout: number;
  retryAttempts: number;
}

export interface FirebaseConfig {
  apiKey: string;
  authDomain: string;
  projectId: string;
  storageBucket: string;
  messagingSenderId: string;
  appId: string;
}

@Injectable({
  providedIn: 'root',
})
export class ConfigService {
  private readonly _isDevelopment = signal(environment.production === false);
  private readonly _apiConfig = signal<ApiConfig>({
    baseUrl: environment.apiUrl || 'http://localhost:3000/api',
    timeout: 30000,
    retryAttempts: 3,
  });
  private readonly _firebaseConfig = signal<FirebaseConfig>({
    apiKey: environment.firebase.apiKey,
    authDomain: environment.firebase.authDomain,
    projectId: environment.firebase.projectId,
    storageBucket: environment.firebase.storageBucket,
    messagingSenderId: environment.firebase.messagingSenderId,
    appId: environment.firebase.appId,
  });

  readonly isDevelopment = this._isDevelopment.asReadonly();
  readonly apiConfig = this._apiConfig.asReadonly();
  readonly firebaseConfig = this._firebaseConfig.asReadonly();

  readonly apiBaseUrl = computed(() => this.apiConfig().baseUrl);
  readonly apiTimeout = computed(() => this.apiConfig().timeout);
  readonly apiRetryAttempts = computed(() => this.apiConfig().retryAttempts);

  getApiUrl(endpoint: string): string {
    const baseUrl = this.apiBaseUrl();
    const cleanEndpoint = endpoint.startsWith('/') ? endpoint.slice(1) : endpoint;
    return `${baseUrl}/${cleanEndpoint}`;
  }

  isFeatureEnabled(feature: string): boolean {
    if (this.isDevelopment()) {
      return true;
    }

    return environment.features?.[feature] ?? false;
  }

  getEnvironmentVariable(key: string): string | undefined {
    return (environment as unknown as Record<string, unknown>)[key] as string | undefined;
  }
}

import { HttpClient, HttpErrorResponse, HttpHeaders, HttpParams } from '@angular/common/http';
import { inject, Injectable, signal } from '@angular/core';
import { Observable, throwError } from 'rxjs';
import { catchError, map, retry, timeout } from 'rxjs/operators';

import { ConfigService } from '../config/config.service';
import { NotificationService } from '../notification/notification.service';

export interface ApiResponse<T = unknown> {
  data: T;
  meta?: {
    count?: number;
    page?: number;
    pageSize?: number;
    hasNext?: boolean;
  };
}

export interface ApiError {
  message: string;
  status: number;
  code?: string;
}

@Injectable({
  providedIn: 'root',
})
export class ApiService {
  private readonly http = inject(HttpClient);
  private readonly configService = inject(ConfigService);
  private readonly notificationService = inject(NotificationService);

  private readonly _isLoading = signal<boolean>(false);
  private readonly _error = signal<ApiError | null>(null);

  readonly isLoading = this._isLoading.asReadonly();
  readonly error = this._error.asReadonly();

  private get timeout(): number {
    return this.configService.apiTimeout();
  }

  private get retryAttempts(): number {
    return this.configService.apiRetryAttempts();
  }

  private createHeaders(additionalHeaders?: Record<string, string>): HttpHeaders {
    const headers: Record<string, string> = {
      'Content-Type': 'application/json',
      ...additionalHeaders,
    };

    return new HttpHeaders(headers);
  }

  private handleError(error: HttpErrorResponse): Observable<never> {
    const apiError: ApiError = {
      message: error.message || 'Erro desconhecido',
      status: error.status,
      code: error.error?.code,
    };

    this._error.set(apiError);
    return throwError(() => apiError);
  }

  private executeRequest<T>(request: Observable<T>, showLoading = true): Observable<T> {
    if (showLoading) {
      this._isLoading.set(true);
      this.notificationService.setLoading(true);
    }

    return request.pipe(
      timeout(this.timeout),
      retry(this.retryAttempts),
      catchError((error) => {
        this._isLoading.set(false);
        this.notificationService.setLoading(false);
        return this.handleError(error);
      }),
      map((response) => {
        this._isLoading.set(false);
        this.notificationService.setLoading(false);
        this._error.set(null);
        return response;
      })
    );
  }

  get<T>(
    endpoint: string,
    params?: Record<string, string | number | boolean>
  ): Observable<ApiResponse<T>> {
    const url = this.configService.getApiUrl(endpoint);
    const httpParams = params ? new HttpParams({ fromObject: params }) : undefined;
    const headers = this.createHeaders();

    const request = this.http.get<ApiResponse<T>>(url, {
      params: httpParams,
      headers,
    });

    return this.executeRequest(request);
  }

  post<T>(
    endpoint: string,
    body: unknown,
    params?: Record<string, string | number | boolean>
  ): Observable<ApiResponse<T>> {
    const url = this.configService.getApiUrl(endpoint);
    const httpParams = params ? new HttpParams({ fromObject: params }) : undefined;
    const headers = this.createHeaders();

    const request = this.http.post<ApiResponse<T>>(url, body, {
      params: httpParams,
      headers,
    });

    return this.executeRequest(request);
  }

  put<T>(
    endpoint: string,
    body: unknown,
    params?: Record<string, string | number | boolean>
  ): Observable<ApiResponse<T>> {
    const url = this.configService.getApiUrl(endpoint);
    const httpParams = params ? new HttpParams({ fromObject: params }) : undefined;
    const headers = this.createHeaders();

    const request = this.http.put<ApiResponse<T>>(url, body, {
      params: httpParams,
      headers,
    });

    return this.executeRequest(request);
  }

  patch<T>(
    endpoint: string,
    body: unknown,
    params?: Record<string, string | number | boolean>
  ): Observable<ApiResponse<T>> {
    const url = this.configService.getApiUrl(endpoint);
    const httpParams = params ? new HttpParams({ fromObject: params }) : undefined;
    const headers = this.createHeaders();

    const request = this.http.patch<ApiResponse<T>>(url, body, {
      params: httpParams,
      headers,
    });

    return this.executeRequest(request);
  }

  delete<T>(
    endpoint: string,
    params?: Record<string, string | number | boolean>
  ): Observable<ApiResponse<T>> {
    const url = this.configService.getApiUrl(endpoint);
    const httpParams = params ? new HttpParams({ fromObject: params }) : undefined;
    const headers = this.createHeaders();

    const request = this.http.delete<ApiResponse<T>>(url, {
      params: httpParams,
      headers,
    });

    return this.executeRequest(request);
  }

  getRaw<T>(endpoint: string, params?: Record<string, string | number | boolean>): Observable<T> {
    const url = this.configService.getApiUrl(endpoint);
    const httpParams = params ? new HttpParams({ fromObject: params }) : undefined;
    const headers = this.createHeaders();

    const request = this.http.get<T>(url, {
      params: httpParams,
      headers,
    });

    return this.executeRequest(request);
  }

  postRaw<T>(
    endpoint: string,
    body: unknown,
    params?: Record<string, string | number | boolean>
  ): Observable<T> {
    const url = this.configService.getApiUrl(endpoint);
    const httpParams = params ? new HttpParams({ fromObject: params }) : undefined;
    const headers = this.createHeaders();

    const request = this.http.post<T>(url, body, {
      params: httpParams,
      headers,
    });

    return this.executeRequest(request);
  }

  clearError(): void {
    this._error.set(null);
  }

  setLoading(loading: boolean): void {
    this._isLoading.set(loading);
  }
}

