export class OsGoalProgressComponent {
  readonly goalData = input.required<GoalProgressData>();
  readonly variant = input<GoalProgressVariant>('default');
  readonly size = input<GoalProgressSize>('medium');
  readonly theme = input<GoalProgressTheme>('light');
  readonly showIcon = input(true);
  readonly showDescription = input(true);
  readonly showCategory = input(true);
  readonly showStats = input(true);
  readonly showPercentage = input(true);
  readonly showDeadline = input(true);
  readonly showActions = input(false);
  readonly animated = input(true);
  readonly actions = input<{ label: string; variant?: string; disabled?: boolean }[]>([]);
  readonly ariaLabel = input<string | null>(null);
  readonly showCelebration = input(true);
  readonly showMilestones = input(true);
  readonly celebrationText = input('Meta alcanada! ');
  readonly enableHapticFeedback = input(false);

  readonly goalClick = output<GoalProgressData>();
  readonly actionClick = output<{
    action: { label: string; variant?: string; disabled?: boolean };
    goal: GoalProgressData;
  }>();
  readonly milestoneReached = output<{
    milestone: GoalProgressMilestone;
    goal: GoalProgressData;
  }>();
  readonly goalCompleted = output<GoalProgressData>();

  readonly titleId = signal(`goal-title-${Math.random().toString(36).substr(2, 9)}`);
  readonly descriptionId = signal(`goal-description-${Math.random().toString(36).substr(2, 9)}`);
  readonly isCelebrating = signal(false);
  readonly reachedMilestones = signal<Set<GoalProgressMilestone>>(new Set());

  readonly progressPercentage = computed(() => {
    const data = this.goalData();
    if (data.targetAmount <= 0) return 0;
    return Math.min(Math.round((data.currentAmount / data.targetAmount) * 100), 100);
  });

  readonly remainingAmount = computed(() => {
    const data = this.goalData();
    return Math.max(data.targetAmount - data.currentAmount, 0);
  });

  readonly isCompleted = computed(() => {
    return this.progressPercentage() >= 100;
  });

  readonly isOverdue = computed(() => {
    const data = this.goalData();
    if (!data.deadline) return false;
    return new Date() > data.deadline && !this.isCompleted();
  });

  readonly currentMilestone = computed((): GoalProgressMilestone | null => {
    const percentage = this.progressPercentage();
    if (percentage >= 100) return 100;
    if (percentage >= 90) return 90;
    if (percentage >= 75) return 75;
    if (percentage >= 50) return 50;
    if (percentage >= 25) return 25;
    return null;
  });

  readonly shouldShowCelebration = computed(() => {
    return this.showCelebration() && this.isCompleted() && !this.isCelebrating();
  });

  readonly shouldShowMilestone = computed(() => {
    if (!this.showMilestones()) return false;
    const current = this.currentMilestone();
    const reached = this.reachedMilestones();
    return current !== null && !reached.has(current);
  });

  readonly goalClasses = computed(() => {
    const classes = ['os-goal-progress'];
    classes.push(`os-goal-progress--${this.variant()}`);
    classes.push(`os-goal-progress--${this.size()}`);

    if (this.theme() === 'dark') {
      classes.push('os-goal-progress--dark');
    }

    if (this.isCompleted()) {
      classes.push('os-goal-progress--completed');
    }

    if (this.isOverdue()) {
      classes.push('os-goal-progress--overdue');
    }

    if (this.goalData().priority) {
      classes.push(`os-goal-progress--priority-${this.goalData().priority}`);
    }

    return classes.join(' ');
  });

  readonly iconName = computed(() => {
    if (this.isCompleted()) return 'check-circle';
    if (this.isOverdue()) return 'alert-circle';
    return 'target';
  });

  readonly iconSize = computed(() => {
    const sizeMap: Record<string, 'sm' | 'md' | 'lg'> = {
      small: 'sm',
      medium: 'md',
      large: 'lg',
    };
    return sizeMap[this.size()];
  });

  readonly progressVariant = computed(() => {
    if (this.isCompleted()) return 'success';
    if (this.isOverdue()) return 'danger';
    if (this.goalData().priority === 'high') return 'warning';
    return 'primary';
  });

  readonly progressSize = computed(() => {
    const sizeMap: Record<string, 'small' | 'medium' | 'large'> = {
      small: 'small',
      medium: 'medium',
      large: 'large',
    };
    return sizeMap[this.size()];
  });

  readonly currentAmountVariant = computed((): OsMoneyDisplayVariant => {
    if (this.isCompleted()) return 'success';
    if (this.isOverdue()) return 'error';
    return 'default';
  });

  readonly targetAmountVariant = computed((): OsMoneyDisplayVariant => {
    return 'default';
  });

  readonly amountSize = computed(() => {
    const sizeMap: Record<string, 'small' | 'medium' | 'large'> = {
      small: 'small',
      medium: 'medium',
      large: 'large',
    };
    return sizeMap[this.size()];
  });

  readonly formattedDeadline = computed(() => {
    const deadline = this.goalData().deadline;
    if (!deadline) return '';

    const now = new Date();
    const diffTime = deadline.getTime() - now.getTime();
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays < 0) return 'Vencido';
    if (diffDays === 0) return 'Hoje';
    if (diffDays === 1) return 'Amanh';
    if (diffDays <= 7) return `${diffDays} dias`;
    if (diffDays <= 30) return `${Math.ceil(diffDays / 7)} semanas`;
    return `${Math.ceil(diffDays / 30)} meses`;
  });

  constructor() {
    effect(() => {
      const currentMilestone = this.currentMilestone();
      const reachedMilestones = this.reachedMilestones();
      const shouldShowMilestone = this.shouldShowMilestone();
      const shouldShowCelebration = this.shouldShowCelebration();

      if (shouldShowMilestone && currentMilestone !== null) {
        const newReached = new Set(reachedMilestones);
        newReached.add(currentMilestone);
        this.reachedMilestones.set(newReached);

        this.milestoneReached.emit({
          milestone: currentMilestone,
          goal: this.goalData(),
        });

        if (this.enableHapticFeedback() && 'vibrate' in navigator) {
          navigator.vibrate(100);
        }
      }

      if (shouldShowCelebration) {
        this.isCelebrating.set(true);
        this.goalCompleted.emit(this.goalData());

        if (this.enableHapticFeedback() && 'vibrate' in navigator) {
          navigator.vibrate([100, 50, 100]);
        }

        setTimeout(() => {
          this.isCelebrating.set(false);
        }, 3000);
      }
    });
  }

  onGoalClick(): void {
    this.goalClick.emit(this.goalData());
  }

  onActionClick(action: { label: string; variant?: string; disabled?: boolean }): void {
    this.actionClick.emit({ action, goal: this.goalData() });
  }
}

import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component, computed, input, output } from '@angular/core';

import { OsBadgeComponent } from '../../atoms/os-badge/os-badge.component';
import { OsIconComponent } from '../../atoms/os-icon/os-icon.component';
import { OsProgressBarComponent } from '../../atoms/os-progress-bar/os-progress-bar.component';
import { OsSpinnerComponent } from '../../atoms/os-spinner/os-spinner.component';
import { OsCardComponent } from '../../molecules/os-card/os-card.component';
import { OsMoneyDisplayComponent } from '../../molecules/os-money-display/os-money-display.component';

export interface GoalTrackerData {
  id: string;
  title: string;
  description?: string;
  targetAmount: number;
  currentAmount: number;
  currency: string;
  deadline?: Date;
  startDate: Date;
  lastUpdated: Date;
  status: 'active' | 'completed' | 'paused' | 'cancelled';
  priority: 'low' | 'medium' | 'high';
  category?: string;
  monthlyContribution?: number;
  progressHistory: ProgressHistory[];
}

export interface ProgressHistory {
  date: Date;
  amount: number;
  percentage: number;
  note?: string;
}

@Component({
  selector: 'os-goal-tracker',
  standalone: true,
  imports: [
    CommonModule,
    OsCardComponent,
    OsMoneyDisplayComponent,
    OsBadgeComponent,
    OsIconComponent,
    OsProgressBarComponent,
    OsSpinnerComponent,
  ],
  templateUrl: './os-goal-tracker.component.html',
  styleUrls: ['./os-goal-tracker.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class OsGoalTrackerComponent {
  // Inputs
  goalData = input.required<GoalTrackerData | null>();
  variant = input<'default' | 'compact' | 'detailed'>('default');
  size = input<'small' | 'medium' | 'large'>('medium');
  theme = input<'light' | 'dark'>('light');
  showTimeline = input<boolean>(true);
  showHistory = input<boolean>(true);
  showContribution = input<boolean>(true);
  showStatus = input<boolean>(true);
  loading = input<boolean>(false);
  clickable = input<boolean>(false);

  // Outputs
  readonly goalClick = output<GoalTrackerData>();
  readonly refreshClick = output<void>();
  readonly actionClick = output<{ action: string; goal: GoalTrackerData }>();

  // Additional properties for template
  readonly ariaLabel = input<string | null>(null);

  // Computed properties
  readonly progressPercentage = computed(() => {
    const data = this.goalData();
    if (!data || data.targetAmount <= 0) return 0;
    return Math.min(Math.round((data.currentAmount / data.targetAmount) * 100), 100);
  });

  readonly remainingAmount = computed(() => {
    const data = this.goalData();
    if (!data) return 0;
    return Math.max(data.targetAmount - data.currentAmount, 0);
  });

  readonly isCompleted = computed(() => {
    const data = this.goalData();
    return data ? data.status === 'completed' || this.progressPercentage() >= 100 : false;
  });

  readonly isOverdue = computed(() => {
    const data = this.goalData();
    if (!data || !data.deadline || this.isCompleted()) return false;
    return new Date() > data.deadline;
  });

  readonly statusInfo = computed(() => {
    const data = this.goalData();
    if (!data) return { type: 'info' as const, label: 'N/A', icon: 'info' };

    switch (data.status) {
      case 'active':
        return this.isOverdue()
          ? { type: 'error' as const, label: 'Atrasado', icon: 'warning' }
          : { type: 'success' as const, label: 'Ativo', icon: 'play_circle' };
      case 'completed':
        return { type: 'success' as const, label: 'Concludo', icon: 'check_circle' };
      case 'paused':
        return { type: 'warning' as const, label: 'Pausado', icon: 'pause_circle' };
      case 'cancelled':
        return { type: 'error' as const, label: 'Cancelado', icon: 'cancel' };
      default:
        return { type: 'info' as const, label: 'N/A', icon: 'info' };
    }
  });

  readonly priorityInfo = computed(() => {
    const data = this.goalData();
    if (!data) return { type: 'info' as const, label: 'N/A', color: 'var(--os-color-info)' };

    switch (data.priority) {
      case 'high':
        return { type: 'error' as const, label: 'Alta', color: 'var(--os-color-error)' };
      case 'medium':
        return { type: 'warning' as const, label: 'Mdia', color: 'var(--os-color-warning)' };
      case 'low':
        return { type: 'success' as const, label: 'Baixa', color: 'var(--os-color-success)' };
      default:
        return { type: 'info' as const, label: 'N/A', color: 'var(--os-color-info)' };
    }
  });

  readonly timelineInfo = computed(() => {
    const data = this.goalData();
    if (!data || !this.showTimeline()) return null;

    return {
      start: data.startDate.toLocaleDateString('pt-BR'),
      deadline: data.deadline ? data.deadline.toLocaleDateString('pt-BR') : 'Sem prazo',
      lastUpdated: data.lastUpdated.toLocaleDateString('pt-BR'),
      daysRemaining: this.getDaysRemaining(),
    };
  });

  readonly recentHistory = computed(() => {
    const data = this.goalData();
    if (!data || !this.showHistory()) return [];

    return data.progressHistory.slice(-3).reverse(); // Last 3 entries
  });

  readonly contributionInfo = computed(() => {
    const data = this.goalData();
    if (!data || !this.showContribution() || !data.monthlyContribution) return null;

    const remaining = this.remainingAmount();
    const monthsNeeded = Math.ceil(remaining / data.monthlyContribution);

    return {
      monthly: data.monthlyContribution,
      monthsNeeded,
      isFeasible: monthsNeeded <= 12, // Consider feasible if less than 1 year
    };
  });

  readonly progressVariant = computed(() => {
    if (this.isCompleted()) return 'success';
    if (this.isOverdue()) return 'danger';
    const data = this.goalData();
    if (data?.priority === 'high') return 'warning';
    return 'primary';
  });

  readonly progressSize = computed(() => {
    const sizeMap: Record<string, 'small' | 'medium' | 'large'> = {
      small: 'small',
      medium: 'medium',
      large: 'large',
    };
    return sizeMap[this.size()];
  });

  readonly iconSize = computed(() => {
    const sizeMap: Record<string, 'sm' | 'md' | 'lg'> = {
      small: 'sm',
      medium: 'md',
      large: 'lg',
    };
    return sizeMap[this.size()];
  });

  readonly badgeSize = computed(() => {
    const sizeMap: Record<string, 'sm' | 'md' | 'lg'> = {
      small: 'sm',
      medium: 'md',
      large: 'lg',
    };
    return sizeMap[this.size()];
  });

  readonly moneySize = computed(() => {
    const sizeMap: Record<string, 'small' | 'medium' | 'large'> = {
      small: 'small',
      medium: 'medium',
      large: 'large',
    };
    return sizeMap[this.size()];
  });

  readonly goalClasses = computed(() => {
    const classes = ['os-goal-tracker'];
    classes.push(`os-goal-tracker--${this.variant()}`);
    classes.push(`os-goal-tracker--${this.size()}`);

    if (this.theme() === 'dark') {
      classes.push('os-goal-tracker--dark');
    }

    if (this.isCompleted()) {
      classes.push('os-goal-tracker--completed');
    }

    if (this.isOverdue()) {
      classes.push('os-goal-tracker--overdue');
    }

    return classes.join(' ');
  });

  readonly cardVariant = computed(() => {
    const data = this.goalData();
    if (!data) return 'default';

    if (this.isCompleted()) return 'elevated';
    if (this.isOverdue()) return 'outlined';
    if (data.priority === 'high') return 'elevated';
    return 'default';
  });

  // Methods
  onCardClick(): void {
    const data = this.goalData();
    if (this.clickable() && data) {
      this.goalClick.emit(data);
    }
  }

  onRefreshClick(): void {
    this.refreshClick.emit();
  }

  onActionClick(action: string): void {
    const data = this.goalData();
    if (data) {
      this.actionClick.emit({ action, goal: data });
    }
  }

  getDaysRemaining(): number {
    const data = this.goalData();
    if (!data || !data.deadline) return 0;

    const now = new Date();
    const diffTime = data.deadline.getTime() - now.getTime();
    return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  }

  formatHistoryDate(date: Date): string {
    return date.toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
    });
  }

  getStatusColor(): string {
    const status = this.statusInfo();
    switch (status.type) {
      case 'success':
        return 'var(--os-color-success)';
      case 'warning':
        return 'var(--os-color-warning)';
      case 'error':
        return 'var(--os-color-error)';
      default:
        return 'var(--os-color-info)';
    }
  }
}

import { Component, computed, input, output, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { ChangeDetectionStrategy } from '@angular/core';
import { OsButtonComponent } from '../../atoms/os-button/os-button.component';
import { OsAvatarComponent } from '../../atoms/os-avatar/os-avatar.component';
import { OsNavigationItemComponent } from '../../molecules/os-navigation-item/os-navigation-item.component';
import { OsIconComponent } from '../../atoms/os-icon/os-icon.component';

export interface HeaderNavigationItem {
  label: string;
  icon?: string;
  route?: string;
  active?: boolean;
  badge?: number;
  disabled?: boolean;
}

export interface HeaderUserMenu {
  label: string;
  icon?: string;
  route?: string;
  action?: string;
  disabled?: boolean;
  divider?: boolean;
}

export interface HeaderAction {
  label: string;
  icon?: string;
  variant?: 'primary' | 'secondary' | 'tertiary' | 'danger';
  size?: 'small' | 'medium' | 'large';
  disabled?: boolean;
  loading?: boolean;
}

@Component({
  selector: 'os-header',
  standalone: true,
  imports: [
    CommonModule,
    RouterModule,
    OsButtonComponent,
    OsAvatarComponent,
    OsNavigationItemComponent,
    OsIconComponent,
  ],
  template: `
    <header class="os-header" [class]="headerClasses()" [attr.aria-label]="ariaLabel()">
      <div class="os-header__container">
        <!-- Logo/Brand Section -->
        <div class="os-header__brand">
          @if (logo()) {
          <a
            class="os-header__logo"
            [routerLink]="logoRoute()"
            [attr.aria-label]="logoAlt()"
            (click)="onLogoClick($event)"
          >
            @if (logoImage()) {
            <img [src]="logoImage()" [alt]="logoAlt()" class="os-header__logo-image" />
            } @else {
            <span class="os-header__logo-text">{{ logoText() }}</span>
            }
          </a>
          } @else if (logoText()) {
          <div class="os-header__logo-text-only">{{ logoText() }}</div>
          }
        </div>

        <!-- Navigation Section -->
        @if (navigationItems().length > 0) {
        <nav class="os-header__navigation" [attr.aria-label]="navigationAriaLabel()">
          <ul class="os-header__nav-list">
            @for (item of navigationItems(); track item.label) {
            <li class="os-header__nav-item">
              <os-navigation-item
                [label]="item.label"
                [icon]="item.icon || ''"
                [routerLink]="item.route"
                [active]="item.active || false"
                [badge]="item.badge || null"
                [disabled]="item.disabled || false"
                [variant]="getNavigationVariant()"
                [size]="getNavigationSize()"
                (itemClick)="onNavigationClick(item, $event)"
              />
            </li>
            }
          </ul>
        </nav>
        }

        <!-- Actions Section -->
        <div class="os-header__actions">
          @if (actions().length > 0) {
          <div class="os-header__action-buttons">
            @for (action of actions(); track action.label) {
            <os-button
              [variant]="action.variant || 'secondary'"
              [size]="action.size || 'medium'"
              [disabled]="action.disabled || false"
              [loading]="action.loading || false"
              [icon]="action.icon || ''"
              (buttonClick)="onActionClick(action, $event)"
            >
              {{ action.label }}
            </os-button>
            }
          </div>
          }

          <!-- User Menu -->
          @if (showUserMenu()) {
          <div class="os-header__user-menu">
            <button
              class="os-header__user-trigger"
              [class]="userTriggerClasses()"
              [disabled]="userMenuDisabled()"
              [attr.aria-label]="userMenuAriaLabel()"
              [attr.aria-expanded]="userMenuOpen()"
              [attr.aria-haspopup]="'menu'"
              (click)="toggleUserMenu()"
            >
              <os-avatar
                [image]="userAvatar() || ''"
                [initials]="userInitials() || ''"
                [size]="getAvatarSize()"
                [variant]="'circle'"
                [ariaLabel]="userName() || 'User'"
              />
              @if (showUserInfo()) {
              <div class="os-header__user-info">
                <span class="os-header__user-name">{{ userName() }}</span>
                @if (userRole()) {
                <span class="os-header__user-role">{{ userRole() }}</span>
                }
              </div>
              }
              <os-icon
                name="chevron-down"
                size="sm"
                class="os-header__user-chevron"
                [class]="userChevronClasses()"
                [attr.aria-hidden]="true"
              />
            </button>

            @if (userMenuOpen()) {
            <div class="os-header__user-dropdown" [attr.role]="'menu'">
              @for (menuItem of userMenuItems(); track menuItem.label) { @if (menuItem.divider) {
              <div class="os-header__user-divider" role="separator"></div>
              } @else {
              <button
                class="os-header__user-menu-item"
                [class]="getUserMenuItemClasses(menuItem)"
                [disabled]="menuItem.disabled || false"
                [attr.aria-label]="menuItem.label"
                (click)="onUserMenuItemClick(menuItem, $event)"
              >
                @if (menuItem.icon) {
                <os-icon
                  [name]="menuItem.icon"
                  size="sm"
                  class="os-header__user-menu-icon"
                  [attr.aria-hidden]="true"
                />
                }
                <span class="os-header__user-menu-text">{{ menuItem.label }}</span>
              </button>
              } }
            </div>
            }
          </div>
          }

          <!-- Mobile Menu Toggle -->
          @if (showMobileMenu()) {
          <button
            class="os-header__mobile-toggle"
            [class]="mobileToggleClasses()"
            [attr.aria-label]="mobileMenuAriaLabel()"
            [attr.aria-expanded]="mobileMenuOpen()"
            (click)="toggleMobileMenu()"
          >
            <os-icon
              [name]="mobileMenuOpen() ? 'close' : 'menu'"
              size="md"
              [attr.aria-hidden]="true"
            />
          </button>
          }
        </div>
      </div>

      <!-- Mobile Navigation -->
      @if (showMobileMenu() && mobileMenuOpen()) {
      <div class="os-header__mobile-navigation" [attr.role]="'navigation'">
        <nav class="os-header__mobile-nav" [attr.aria-label]="mobileNavigationAriaLabel()">
          <ul class="os-header__mobile-nav-list">
            @for (item of navigationItems(); track item.label) {
            <li class="os-header__mobile-nav-item">
              <os-navigation-item
                [label]="item.label"
                [icon]="item.icon || ''"
                [routerLink]="item.route"
                [active]="item.active || false"
                [badge]="item.badge || null"
                [disabled]="item.disabled || false"
                [variant]="getMobileNavigationVariant()"
                [size]="getMobileNavigationSize()"
                (itemClick)="onMobileNavigationClick(item, $event)"
              />
            </li>
            }
          </ul>
        </nav>
      </div>
      }
    </header>
  `,
  styleUrls: ['./os-header.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class OsHeaderComponent {
  // Logo/Brand inputs
  readonly logo = input<string | null>(null);
  readonly logoText = input<string | null>(null);
  readonly logoImage = input<string | null>(null);
  readonly logoRoute = input<string>('/');
  readonly logoAlt = input<string>('Logo');

  // Navigation inputs
  readonly navigationItems = input<HeaderNavigationItem[]>([]);
  readonly navigationAriaLabel = input<string>('Main navigation');

  // User menu inputs
  readonly showUserMenu = input(false);
  readonly userName = input<string | null>(null);
  readonly userRole = input<string | null>(null);
  readonly userAvatar = input<string | null>(null);
  readonly userInitials = input<string | null>(null);
  readonly userMenuItems = input<HeaderUserMenu[]>([]);
  readonly userMenuDisabled = input(false);
  readonly userMenuAriaLabel = input<string>('User menu');

  // Actions inputs
  readonly actions = input<HeaderAction[]>([]);

  // Mobile menu inputs
  readonly showMobileMenu = input(false);
  readonly mobileMenuAriaLabel = input<string>('Mobile menu');
  readonly mobileNavigationAriaLabel = input<string>('Mobile navigation');

  // Styling inputs
  readonly variant = input<'default' | 'compact' | 'extended' | 'minimal'>('default');
  readonly size = input<'small' | 'medium' | 'large'>('medium');
  readonly theme = input<'light' | 'dark'>('light');
  readonly sticky = input(false);
  readonly ariaLabel = input<string | null>(null);

  // State signals
  readonly userMenuOpen = signal(false);
  readonly mobileMenuOpen = signal(false);

  // Outputs
  readonly logoClick = output<MouseEvent>();
  readonly navigationClick = output<{ item: HeaderNavigationItem; event: MouseEvent }>();
  readonly actionClick = output<{ action: HeaderAction; event: MouseEvent }>();
  readonly userMenuItemClick = output<{ item: HeaderUserMenu; event: MouseEvent }>();
  readonly mobileNavigationClick = output<{ item: HeaderNavigationItem; event: MouseEvent }>();
  readonly mobileMenuToggle = output<boolean>();

  readonly headerClasses = computed(() => {
    const classes = ['os-header'];
    classes.push(`os-header--${this.variant()}`);
    classes.push(`os-header--${this.size()}`);

    if (this.theme() === 'dark') {
      classes.push('os-header--dark');
    }

    if (this.sticky()) {
      classes.push('os-header--sticky');
    }

    if (this.navigationItems().length > 0) {
      classes.push('os-header--with-navigation');
    }

    if (this.actions().length > 0) {
      classes.push('os-header--with-actions');
    }

    if (this.showUserMenu()) {
      classes.push('os-header--with-user-menu');
    }

    if (this.showMobileMenu()) {
      classes.push('os-header--with-mobile-menu');
    }

    if (this.mobileMenuOpen()) {
      classes.push('os-header--mobile-open');
    }

    return classes.join(' ');
  });

  readonly userTriggerClasses = computed(() => {
    const classes = ['os-header__user-trigger'];

    if (this.userMenuOpen()) {
      classes.push('os-header__user-trigger--open');
    }

    if (this.userMenuDisabled()) {
      classes.push('os-header__user-trigger--disabled');
    }

    return classes.join(' ');
  });

  readonly userChevronClasses = computed(() => {
    const classes = ['os-header__user-chevron'];

    if (this.userMenuOpen()) {
      classes.push('os-header__user-chevron--open');
    }

    return classes.join(' ');
  });

  readonly mobileToggleClasses = computed(() => {
    const classes = ['os-header__mobile-toggle'];

    if (this.mobileMenuOpen()) {
      classes.push('os-header__mobile-toggle--open');
    }

    return classes.join(' ');
  });

  readonly showUserInfo = computed(() => {
    return this.variant() === 'extended' && (this.userName() || this.userRole());
  });

  readonly getAvatarSize = computed(() => {
    const sizeMap: Record<string, 'sm' | 'md' | 'lg'> = {
      small: 'sm',
      medium: 'md',
      large: 'lg',
    };
    return sizeMap[this.size()];
  });

  readonly getNavigationVariant = computed(() => {
    return this.theme() === 'dark' ? ('accent' as const) : ('default' as const);
  });

  readonly getNavigationSize = computed(() => {
    const sizeMap: Record<string, 'small' | 'medium' | 'large'> = {
      small: 'small',
      medium: 'medium',
      large: 'large',
    };
    return sizeMap[this.size()] as 'small' | 'medium' | 'large';
  });

  readonly getMobileNavigationVariant = computed(() => {
    return 'primary' as const;
  });

  readonly getMobileNavigationSize = computed(() => {
    return 'large' as const;
  });

  getUserMenuItemClasses(menuItem: HeaderUserMenu): string {
    const classes = ['os-header__user-menu-item'];

    if (menuItem.disabled) {
      classes.push('os-header__user-menu-item--disabled');
    }

    return classes.join(' ');
  }

  onLogoClick(event: MouseEvent): void {
    this.logoClick.emit(event);
  }

  onNavigationClick(item: HeaderNavigationItem, event: MouseEvent): void {
    this.navigationClick.emit({ item, event });
  }

  onActionClick(action: HeaderAction, event: MouseEvent): void {
    this.actionClick.emit({ action, event });
  }

  onUserMenuItemClick(menuItem: HeaderUserMenu, event: MouseEvent): void {
    if (!menuItem.disabled) {
      this.userMenuItemClick.emit({ item: menuItem, event });
      this.userMenuOpen.set(false);
    }
  }

  onMobileNavigationClick(item: HeaderNavigationItem, event: MouseEvent): void {
    this.mobileNavigationClick.emit({ item, event });
    this.mobileMenuOpen.set(false);
  }

  toggleUserMenu(): void {
    if (!this.userMenuDisabled()) {
      this.userMenuOpen.update((open) => !open);
    }
  }

  toggleMobileMenu(): void {
    this.mobileMenuOpen.update((open) => !open);
    this.mobileMenuToggle.emit(this.mobileMenuOpen());
  }
}

import { Component, computed, input, output, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { ChangeDetectionStrategy } from '@angular/core';
import { OsButtonComponent } from '../../atoms/os-button/os-button.component';
import { OsAvatarComponent } from '../../atoms/os-avatar/os-avatar.component';
import { OsNavigationItemComponent } from '../../molecules/os-navigation-item/os-navigation-item.component';
import { OsIconComponent } from '../../atoms/os-icon/os-icon.component';

export interface HeaderNavigationItem {
  label: string;
  icon?: string;
  route?: string;
  active?: boolean;
  badge?: number;
  disabled?: boolean;
}

export interface HeaderUserMenu {
