    CommonModule,
    RouterModule,
    OsPageHeaderComponent,
    OsDataGridComponent,
    OsFilterBarComponent,
    OsButtonComponent,
    OsIconComponent,
    OsSpinnerComponent,
  ],
})
export class OsListTemplateComponent {
  // Inputs
  variant = input<'default' | 'compact' | 'detailed'>('default');
  size = input<'small' | 'medium' | 'large'>('medium');
  theme = input<'light' | 'dark'>('light');

  // Header
  title = input<string>('');
