  }

  onGridActionClick(action: {
    key: string;
    label: string;
    icon?: string;
    color?: 'primary' | 'secondary' | 'warn';
  }) {
    this.gridActionClick.emit(action);
  }

  onFooterActionClick(action: ListTemplateAction, event: MouseEvent) {
    this.footerActionClick.emit({ action, event });
  }

  onFilterChange(filters: { field?: string; operator?: string; value?: unknown }[]) {
    // Convert OsDataGridFilter[] to ListTemplateFilter[]
    const convertedFilters: ListTemplateFilter[] = filters.map((filter) => ({
      field: filter.field || '',
      operator: (filter.operator as ListTemplateFilter['operator']) || 'equals',
      value: (filter.value as ListTemplateFilter['value']) || '',
    }));
    this.filterChange.emit(convertedFilters);
  }

  onSortChange(sort: { field?: string; direction?: 'asc' | 'desc' }) {
    // Convert OsDataGridSort to our format
    const convertedSort = {
      field: sort.field || '',
      direction: sort.direction || 'asc',
    } as { field: string; direction: 'asc' | 'desc' };
    this.sortChange.emit(convertedSort);
  }

  onPageChange(page: { page: number; pageSize: number }) {
    this.pageChange.emit(page);
  }

  onRefresh() {
    this.refresh.emit();
  }

  onExport() {
    this.export.emit();
  }

  onAdd() {
    this.add.emit();
  }

  onBackClick(event: MouseEvent) {
    this.backClick.emit(event);
  }

  onEmptyActionClick(event: MouseEvent) {
    this.emptyActionClick.emit(event);
  }

  onClearFilters() {
    this.filterChange.emit([]);
  }

  onApplyFilters() {
    // Filter application logic can be implemented here
    // For now, just emit the current filters
    this.filterChange.emit(this.filters());
  }
}

import { CommonModule } from '@angular/common';
import { Component, computed, input, output, ChangeDetectionStrategy } from '@angular/core';
import { OsModalComponent } from '../../organisms/os-modal/os-modal.component';
import { OsButtonComponent } from '../../atoms/os-button/os-button.component';

export interface ModalTemplateConfig {
  title: string;
  subtitle?: string;
  showCloseButton?: boolean;
  showHeader?: boolean;
  showFooter?: boolean;
  showActions?: boolean;
  closeButtonText?: string;
  confirmButtonText?: string;
  cancelButtonText?: string;
  showConfirmButton?: boolean;
  showCancelButton?: boolean;
  actions?: {
    label: string;
    variant: 'primary' | 'secondary' | 'tertiary' | 'danger';
    size: 'small' | 'medium' | 'large';
    disabled?: boolean;
    loading?: boolean;
    icon?: string;
  }[];
}

@Component({
  selector: 'os-modal-template',
  template: `
    <os-modal
      [variant]="modalVariant()"
      [size]="size()"
      [title]="config().title"
      [subtitle]="config().subtitle || null"
      (close)="onClose()"
      (backdropClick)="onBackdropClick()"
      (escapeKey)="onEscapeKey()"
    >
      <div class="os-modal-template__content" [class]="contentClass()">
        <ng-content />
      </div>

      @if (config().showActions ?? true) {
      <div class="os-modal-template__actions" [class]="actionsClass()">
        @if (config().showCancelButton ?? true) {
        <os-button
          [variant]="'secondary'"
          [size]="size()"
          [disabled]="disabled()"
          (click)="onCancel()"
        >
          {{ config().cancelButtonText || 'Cancelar' }}
        </os-button>
        } @if (config().showConfirmButton ?? true) {
        <os-button
          [variant]="'primary'"
          [size]="size()"
          [disabled]="disabled() || !isValid()"
          [loading]="loading()"
          (click)="onConfirm()"
        >
          {{ config().confirmButtonText || 'Confirmar' }}
        </os-button>
        } @for (action of config().actions || []; track action.label) {
        <os-button
          [variant]="action.variant"
          [size]="action.size"
          [disabled]="action.disabled || disabled()"
          [loading]="action.loading || false"
          [icon]="action.icon || ''"
          (click)="onActionClick(action)"
        >
          {{ action.label }}
        </os-button>
        }
      </div>
      }
    </os-modal>
  `,
  styleUrls: ['./os-modal-template.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  imports: [CommonModule, OsModalComponent, OsButtonComponent],
})
export class OsModalTemplateComponent {
  config = input.required<ModalTemplateConfig>();
  size = input<'small' | 'medium' | 'large'>('medium');
  variant = input<'default' | 'compact' | 'detailed'>('default');
  theme = input<'light' | 'dark'>('light');
  disabled = input(false);
  loading = input(false);
  valid = input(true);

  closed = output<void>();
  confirmed = output<void>();
  cancelled = output<void>();
  actionClick = output<{
    label: string;
    variant: 'primary' | 'secondary' | 'tertiary' | 'danger';
    size: 'small' | 'medium' | 'large';
    disabled?: boolean;
    loading?: boolean;
    icon?: string;
  }>();
  backdropClick = output<void>();
  escapeKey = output<void>();

  modalVariant = computed(() => {
    const variant = this.variant();
    return variant === 'compact' ? 'confirmation' : 'default';
  });

  contentClass = computed(() => {
    return [
      'os-modal-template__content',
      `os-modal-template__content--${this.variant()}`,
      `os-modal-template__content--${this.size()}`,
      this.disabled() ? 'os-modal-template__content--disabled' : '',
      this.loading() ? 'os-modal-template__content--loading' : '',
    ]
      .filter(Boolean)
      .join(' ');
  });

  actionsClass = computed(() => {
    return [
      'os-modal-template__actions',
      `os-modal-template__actions--${this.variant()}`,
      `os-modal-template__actions--${this.size()}`,
    ]
      .filter(Boolean)
      .join(' ');
  });

  isValid = computed(() => {
    return this.valid();
  });

  onClose(): void {
    this.closed.emit();
  }

  onConfirm(): void {
    this.confirmed.emit();
  }

  onCancel(): void {
    this.cancelled.emit();
  }

  onActionClick(action: {
    label: string;
    variant: 'primary' | 'secondary' | 'tertiary' | 'danger';
    size: 'small' | 'medium' | 'large';
    disabled?: boolean;
    loading?: boolean;
    icon?: string;
  }): void {
    this.actionClick.emit(action);
  }

  onBackdropClick(): void {
    this.backdropClick.emit();
  }

  onEscapeKey(): void {
    this.escapeKey.emit();
  }
}

import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component, computed, input, output } from '@angular/core';

import { OsBadgeComponent } from '../../atoms/os-badge/os-badge.component';
import { OsIconComponent } from '../../atoms/os-icon/os-icon.component';
import { OsProgressBarComponent } from '../../atoms/os-progress-bar/os-progress-bar.component';
import { OsSpinnerComponent } from '../../atoms/os-spinner/os-spinner.component';
import { OsCardComponent } from '../../molecules/os-card/os-card.component';
import { OsMoneyDisplayComponent } from '../../molecules/os-money-display/os-money-display.component';

export interface BudgetTrackerData {
  id: string;
  name: string;
  totalBudget: number;
  spentAmount: number;
  remainingAmount: number;
  percentage: number;
  status: 'on-track' | 'over-budget' | 'under-budget' | 'completed';
  category: string;
  startDate: Date;
  endDate: Date;
  lastUpdated: Date;
  monthlySpending: MonthlySpending[];
  trends: BudgetTrends;
}

export interface MonthlySpending {
  month: string;
  year: number;
  amount: number;
  percentage: number;
}

export interface BudgetTrends {
  spendingTrend: 'increasing' | 'decreasing' | 'stable';
  projection: number;
  riskLevel: 'low' | 'medium' | 'high';
}

@Component({
  selector: 'os-budget-tracker',
  standalone: true,
  imports: [
    CommonModule,
    OsCardComponent,
    OsMoneyDisplayComponent,
    OsBadgeComponent,
    OsIconComponent,
    OsProgressBarComponent,
    OsSpinnerComponent,
  ],
  templateUrl: './os-budget-tracker.component.html',
  styleUrls: ['./os-budget-tracker.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class OsBudgetTrackerComponent {
  // Inputs
  budgetData = input.required<BudgetTrackerData | null>();
  variant = input<'default' | 'compact' | 'detailed'>('default');
  size = input<'small' | 'medium' | 'large'>('medium');
  showCharts = input<boolean>(true);
  showTrends = input<boolean>(true);
  showProjections = input<boolean>(true);
  showStatus = input<boolean>(true);
  loading = input<boolean>(false);
  clickable = input<boolean>(false);

  // Outputs
  readonly budgetClick = output<BudgetTrackerData>();
  readonly refreshClick = output<void>();
  readonly exportClick = output<void>();

  // Computed properties
  readonly progressPercentage = computed(() => {
    const data = this.budgetData();
    return data ? Math.min(Math.max(data.percentage, 0), 100) : 0;
  });

  readonly statusInfo = computed(() => {
    const data = this.budgetData();
    if (!data) return { type: 'info' as const, label: 'N/A', icon: 'info' };

    switch (data.status) {
      case 'on-track':
        return { type: 'success' as const, label: 'No Prazo', icon: 'check_circle' };
      case 'over-budget':
        return { type: 'error' as const, label: 'Acima do Oramento', icon: 'warning' };
      case 'under-budget':
        return { type: 'warning' as const, label: 'Abaixo do Oramento', icon: 'info' };
      case 'completed':
        return { type: 'success' as const, label: 'Concludo', icon: 'check' };
      default:
        return { type: 'info' as const, label: 'N/A', icon: 'info' };
    }
  });

  readonly isOverBudget = computed(() => {
    const data = this.budgetData();
    return data ? data.spentAmount > data.totalBudget : false;
  });

  readonly isCompleted = computed(() => {
    const data = this.budgetData();
    return data ? data.status === 'completed' : false;
  });

  readonly trendInfo = computed(() => {
    const data = this.budgetData();
    if (!data || !this.showTrends()) return null;

    const trend = data.trends;
    return {
      icon: this.getTrendIcon(trend.spendingTrend),
      label: this.getTrendLabel(trend.spendingTrend),
      color: this.getTrendColor(trend.riskLevel),
      riskLevel: trend.riskLevel,
    };
  });

  readonly projectionInfo = computed(() => {
    const data = this.budgetData();
    if (!data || !this.showProjections()) return null;

    const projection = data.trends.projection;
    const remaining = data.remainingAmount;
    const isProjectedOver = projection > remaining;

    return {
      value: projection,
      isOver: isProjectedOver,
      percentage: data.totalBudget > 0 ? (projection / data.totalBudget) * 100 : 0,
    };
  });

  readonly monthlyData = computed(() => {
    const data = this.budgetData();
    if (!data || !this.showCharts()) return [];

    return data.monthlySpending.slice(-6); // Last 6 months
  });

  readonly formattedDates = computed(() => {
    const data = this.budgetData();
    if (!data) return null;

    return {
      start: data.startDate.toLocaleDateString('pt-BR'),
      end: data.endDate.toLocaleDateString('pt-BR'),
      lastUpdated: data.lastUpdated.toLocaleDateString('pt-BR'),
    };
  });

  // Methods
  onCardClick(): void {
    const data = this.budgetData();
    if (this.clickable() && data) {
      this.budgetClick.emit(data);
    }
  }

  onRefreshClick(): void {
    this.refreshClick.emit();
  }

  onExportClick(): void {
    this.exportClick.emit();
  }

  getStatusColor(): string {
    const status = this.statusInfo();
    switch (status.type) {
      case 'success':
        return 'var(--os-color-success)';
      case 'warning':
        return 'var(--os-color-warning)';
      case 'error':
        return 'var(--os-color-error)';
      default:
        return 'var(--os-color-info)';
    }
  }

  private getTrendIcon(trend: string): string {
    switch (trend) {
      case 'increasing':
        return 'trending_up';
      case 'decreasing':
        return 'trending_down';
      case 'stable':
        return 'trending_flat';
      default:
        return 'trending_flat';
    }
  }

  private getTrendLabel(trend: string): string {
    switch (trend) {
      case 'increasing':
        return 'Crescendo';
      case 'decreasing':
        return 'Diminuindo';
      case 'stable':
        return 'Estvel';
      default:
        return 'N/A';
    }
  }

  private getTrendColor(riskLevel: string): string {
    switch (riskLevel) {
      case 'low':
        return 'var(--os-color-success)';
      case 'medium':
        return 'var(--os-color-warning)';
      case 'high':
        return 'var(--os-color-error)';
      default:
        return 'var(--os-color-info)';
    }
  }

  getMonthName(month: string): string {
    const months = {
      '01': 'Jan',
      '02': 'Fev',
      '03': 'Mar',
      '04': 'Abr',
      '05': 'Mai',
      '06': 'Jun',
      '07': 'Jul',
      '08': 'Ago',
      '09': 'Set',
      '10': 'Out',
      '11': 'Nov',
      '12': 'Dez',
    };
    return months[month as keyof typeof months] || month;
  }
}

import {
  Component,
  Input,
  Output,
  EventEmitter,
  signal,
  computed,
  ChangeDetectionStrategy,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import {
  OsNavigationItemComponent,
  OsNavigationItemVariant,
  OsNavigationItemSize,
} from '../../molecules/os-navigation-item/os-navigation-item.component';

export interface NavigationItem {
  id: string;
  label: string;
  icon?: string;
  route?: string;
  href?: string;
  badge?: string | number;
  disabled?: boolean;
  children?: NavigationItem[];
}

export type NavigationVariant = 'default' | 'minimal' | 'sidebar' | 'tabs';
export type NavigationSize = 'small' | 'medium' | 'large';
export type NavigationOrientation = 'horizontal' | 'vertical';

@Component({
  selector: 'os-navigation',
  standalone: true,
  imports: [CommonModule, RouterModule, OsNavigationItemComponent],
  template: `
    <nav [class]="navigationClasses()" [attr.aria-label]="ariaLabel" role="navigation">
      <!-- Navigation Items -->
      <ul [class]="listClasses()" role="list">
        @for (item of items(); track item.id) {
        <li role="none">
          <os-navigation-item
            [label]="item.label"
            [icon]="item.icon || ''"
            [routerLink]="item.route"
            [variant]="itemVariant()"
            [size]="itemSize()"
            [active]="isActiveItem(item)"
            [disabled]="item.disabled || false"
            [badge]="item.badge ? +item.badge : null"
            [ariaLabel]="item.label"
            (itemClick)="onItemClick(item)"
          >
            {{ item.label }}
          </os-navigation-item>
        </li>
        }
      </ul>

      <!-- Custom Content -->
      @if (showCustomContent()) {
      <div class="os-navigation__custom">
        <ng-content select="[slot=custom]"></ng-content>
      </div>
      }
    </nav>
  `,
  styleUrls: ['./os-navigation.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class OsNavigationComponent {
  // Inputs
  @Input({ required: true }) items = signal<NavigationItem[]>([]);
  @Input() variant = signal<NavigationVariant>('default');
  @Input() size = signal<NavigationSize>('medium');
  @Input() orientation = signal<NavigationOrientation>('horizontal');
  @Input() activeItemId = signal<string | null>(null);
  @Input() ariaLabel = 'Navegao principal';
  @Input() showCustomContent = signal(false);

  // Outputs
  @Output() itemClick = new EventEmitter<NavigationItem>();
  @Output() navigate = new EventEmitter<{ item: NavigationItem; route?: string; href?: string }>();

  // Computed properties
  navigationClasses = computed(() => {
    const base = 'os-navigation';
    const variant = `os-navigation--${this.variant()}`;
    const size = `os-navigation--${this.size()}`;
    const orientation = `os-navigation--${this.orientation()}`;

    return `${base} ${variant} ${size} ${orientation}`;
  });

  listClasses = computed(() => {
    const base = 'os-navigation__list';
    const orientation = `os-navigation__list--${this.orientation()}`;
    const variant = `os-navigation__list--${this.variant()}`;

    return `${base} ${orientation} ${variant}`;
  });

  itemVariant = computed(() => {
    const variantMap: Record<NavigationVariant, OsNavigationItemVariant> = {
      default: 'default',
      minimal: 'secondary',
      sidebar: 'primary',
      tabs: 'accent',
    };
    return variantMap[this.variant()];
  });

  itemSize = computed(() => {
    const sizeMap: Record<NavigationSize, OsNavigationItemSize> = {
      small: 'small',
      medium: 'medium',
      large: 'large',
    };
    return sizeMap[this.size()];
  });

  // Methods
  isActiveItem(item: NavigationItem): boolean {
    const activeId = this.activeItemId();
    return activeId === item.id;
  }

  onItemClick(item: NavigationItem): void {
    if (item.disabled) return;

    this.itemClick.emit(item);

    // Auto-navigate if route or href is provided
    if (item.route || item.href) {
      this.navigate.emit({ item, route: item.route, href: item.href });
    }
  }

  onNavigate(event: { item: NavigationItem; route?: string; href?: string }): void {
    this.navigate.emit(event);
  }
}

import { Injectable, signal, computed } from '@angular/core';
import { OsAlertType } from '../../../shared/ui-components/molecules/os-alert/os-alert.component';

export interface Notification {
  id: string;
  type: OsAlertType;
  title?: string;
  message: string;
  dismissible?: boolean;
  duration?: number;
}

@Injectable({
  providedIn: 'root',
})
export class NotificationService {
  private readonly _notifications = signal<Notification[]>([]);
  private readonly _isLoading = signal<boolean>(false);

  readonly notifications = this._notifications.asReadonly();
  readonly isLoading = this._isLoading.asReadonly();
  readonly hasNotifications = computed(() => this._notifications().length > 0);

  showSuccess(message: string, title?: string, duration = 5000): string {
    return this.addNotification({
      type: 'success',
      message,
      title,
      dismissible: true,
      duration,
    });
  }

  showError(message: string, title?: string, duration = 8000): string {
    return this.addNotification({
      type: 'error',
      message,
      title,
      dismissible: true,
      duration,
    });
  }

  showWarning(message: string, title?: string, duration = 6000): string {
    return this.addNotification({
      type: 'warning',
      message,
      title,
      dismissible: true,
      duration,
    });
  }

  showInfo(message: string, title?: string, duration = 4000): string {
    return this.addNotification({
      type: 'info',
      message,
      title,
      dismissible: true,
      duration,
    });
  }

  setLoading(loading: boolean): void {
    this._isLoading.set(loading);
  }

  dismissNotification(id: string): void {
    this._notifications.update((notifications) =>
      notifications.filter((notification) => notification.id !== id)
    );
  }

  clearAll(): void {
    this._notifications.set([]);
  }

  private addNotification(notification: Omit<Notification, 'id'>): string {
    const id = `notification-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
    const newNotification: Notification = {
      ...notification,
      id,
    };

    this._notifications.update((notifications) => [...notifications, newNotification]);

    if (notification.duration && notification.duration > 0) {
      setTimeout(() => {
        this.dismissNotification(id);
      }, notification.duration);
    }

    return id;
  }
}

import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component, computed, input, output, signal } from '@angular/core';

import { OsButtonComponent } from '../../atoms/os-button/os-button.component';
import {
  OsDataTableAction,
  OsDataTableColumn,
  OsDataTableComponent,
} from '../../molecules/os-data-table/os-data-table.component';
import {
  OsFilterBarComponent,
  OsFilterOption,
} from '../../molecules/os-filter-bar/os-filter-bar.component';

export type OsDataGridSize = 'small' | 'medium' | 'large';
export type OsDataGridVariant = 'default' | 'compact' | 'detailed';

export interface OsDataGridFilter {
  key: string;
  value: string | number | Date | boolean;
  operator?: 'equals' | 'contains' | 'startsWith' | 'endsWith' | 'gt' | 'lt' | 'gte' | 'lte';
}

export interface OsDataGridSort {
  column: string;
  direction: 'asc' | 'desc';
}

export interface OsDataGridPagination {
  page: number;
  pageSize: number;
  total: number;
}

@Component({
  selector: 'os-data-grid',
  standalone: true,
  imports: [CommonModule, OsDataTableComponent, OsFilterBarComponent, OsButtonComponent],
  template: `
    <div class="os-data-grid" [class]="dataGridClasses()">
      <!-- Header -->
      @if (title() || showHeaderActions()) {
      <div class="os-data-grid__header">
        @if (title()) {
        <div class="os-data-grid__title-section">
          <h2 class="os-data-grid__title">{{ title() }}</h2>
          @if (subtitle()) {
          <p class="os-data-grid__subtitle">{{ subtitle() }}</p>
          }
        </div>
        } @if (showHeaderActions()) {
        <div class="os-data-grid__header-actions">
          @if (showRefreshButton()) {
          <os-button
            variant="tertiary"
            [size]="getButtonSize()"
            icon="refresh"
            [loading]="isLoading()"
            (buttonClick)="onRefresh()"
            [attr.aria-label]="'Atualizar dados'"
          >
            Atualizar
          </os-button>
          } @if (showExportButton()) {
          <os-button
            variant="secondary"
            [size]="getButtonSize()"
            icon="download"
            (buttonClick)="onExport()"
            [attr.aria-label]="'Exportar dados'"
          >
            Exportar
          </os-button>
          } @if (showAddButton()) {
          <os-button
            variant="primary"
            [size]="getButtonSize()"
            icon="add"
            (buttonClick)="onAdd()"
            [attr.aria-label]="'Adicionar novo item'"
          >
            {{ addButtonText() }}
          </os-button>
          }
        </div>
        }
      </div>
      }

      <!-- Filters -->
      @if (showFilters() && filterOptions().length > 0) {
      <div class="os-data-grid__filters">
        <os-filter-bar
          [variant]="getFilterBarVariant()"
          [size]="getFilterBarSize()"
          [hasActiveFilters]="hasActiveFilters()"
          (clear)="onClearFilters()"
          (apply)="onApplyFilters()"
        >
          <div class="os-data-grid__filter-content">
            @for (option of filterOptions(); track option.key) {
            <div class="os-data-grid__filter-item">
              <label [for]="'filter-' + option.key" class="os-data-grid__filter-label">{{
                option.label
              }}</label>
              @switch (option.type) { @case ('text') {
              <input
                [id]="'filter-' + option.key"
                type="text"
                class="os-data-grid__filter-input"
                [placeholder]="'Filtrar por ' + option.label"
                [value]="getFilterValue(option.key)"
                (input)="onFilterChange(option.key, $event)"
              />
              } @case ('select') {
              <select
                class="os-data-grid__filter-select"
                [value]="getFilterValue(option.key)"
                (change)="onFilterChange(option.key, $event)"
              >
                <option value="">{{ 'Todos os ' + option.label }}</option>
                @for (opt of option.options; track opt.value) {
                <option [value]="opt.value">{{ opt.label }}</option>
                }
              </select>
              } @case ('date') {
              <input
                type="date"
                class="os-data-grid__filter-input"
                [value]="getFilterValue(option.key)"
                (change)="onFilterChange(option.key, $event)"
              />
              } @case ('number') {
              <input
                type="number"
                class="os-data-grid__filter-input"
                [placeholder]="'Filtrar por ' + option.label"
                [value]="getFilterValue(option.key)"
                (input)="onFilterChange(option.key, $event)"
              />
              } }
            </div>
            }
          </div>
        </os-filter-bar>
      </div>
      }

      <!-- Data Table -->
      <div class="os-data-grid__table">
        <os-data-table
          [data]="filteredData()"
          [columns]="columns()"
          [actions]="tableActions()"
          [size]="getTableSize()"
          [variant]="getTableVariant()"
          [showPagination]="showPagination()"
          [showNoData]="showNoData()"
          [noDataText]="noDataText()"
          [pageSize]="pagination().pageSize"
          [pageIndex]="pagination().page"
          [totalItems]="totalItems()"
          [pageSizeOptions]="pageSizeOptions()"
          [showFirstLastButtons]="showFirstLastButtons()"
          (rowClick)="onRowClick($event)"
          (actionClick)="onTableActionClick($event)"
          (sortChange)="onSortChange($event)"
          (pageChange)="onPageChange($event)"
        />
      </div>

      <!-- Footer -->
      @if (showFooter()) {
      <div class="os-data-grid__footer">
        <div class="os-data-grid__footer-info">
          @if (showItemCount()) {
          <span class="os-data-grid__item-count">
            {{ getItemCountText() }}
          </span>
          } @if (showLastUpdated()) {
          <span class="os-data-grid__last-updated">
            ltima atualizao: {{ lastUpdated() | date : 'dd/MM/yyyy HH:mm' }}
          </span>
          }
        </div>

        @if (showFooterActions()) {
        <div class="os-data-grid__footer-actions">
          <ng-content select="[slot=footer-actions]"></ng-content>
        </div>
        }
      </div>
      }
    </div>
  `,
  styleUrl: './os-data-grid.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush,
  host: {
    class: 'os-data-grid-host',
  },
})
export class OsDataGridComponent {
  // Inputs
  data = input<Record<string, unknown>[]>([]);
  columns = input<OsDataTableColumn[]>([]);
  filterOptions = input<OsFilterOption[]>([]);
  tableActions = input<OsDataTableAction[]>([]);
  title = input<string>('');
  subtitle = input<string>('');
  size = input<OsDataGridSize>('medium');
  variant = input<OsDataGridVariant>('default');

  // Display options
  showHeaderActions = input<boolean>(true);
  showRefreshButton = input<boolean>(true);
  showExportButton = input<boolean>(true);
  showAddButton = input<boolean>(true);
  addButtonText = input<string>('Adicionar');
  showFilters = input<boolean>(true);
  showPagination = input<boolean>(true);
  showNoData = input<boolean>(true);
  noDataText = input<string>('Nenhum dado encontrado');
  showFooter = input<boolean>(true);
  showItemCount = input<boolean>(true);
  showLastUpdated = input<boolean>(true);
  showFooterActions = input<boolean>(false);

  // Pagination
  pageSizeOptions = input<number[]>([5, 10, 25, 50]);
  showFirstLastButtons = input<boolean>(true);

  // Loading state
  isLoading = input<boolean>(false);
  lastUpdated = input<Date>(new Date());

  // Outputs
  rowClick = output<Record<string, unknown>>();
  tableActionClick = output<OsDataTableAction>();
  refresh = output<void>();
  export = output<void>();
  add = output<void>();
  filterChange = output<OsDataGridFilter[]>();
  sortChange = output<OsDataGridSort>();
  pageChange = output<OsDataGridPagination>();

  // Internal state
  private filters = signal<OsDataGridFilter[]>([]);
  private sort = signal<OsDataGridSort | null>(null);
  protected pagination = signal<OsDataGridPagination>({
    page: 0,
    pageSize: 10,
    total: 0,
  });

  // Computed properties
  filteredData = computed(() => {
    let result = [...this.data()];

    // Apply filters
    const activeFilters = this.filters().filter(
      (f) => f.value !== null && f.value !== undefined && f.value !== ''
    );
    if (activeFilters.length > 0) {
      result = result.filter((item) => {
        return activeFilters.every((filter) => {
          const itemValue = this.getNestedValue(item, filter.key);
          return this.matchesFilter(itemValue, filter.value, filter.operator || 'contains');
        });
      });
    }

    // Apply sorting
    if (this.sort()) {
      result = this.applySorting(result, this.sort()!);
    }

    // Apply pagination
    const startIndex = this.pagination().page * this.pagination().pageSize;
    const endIndex = startIndex + this.pagination().pageSize;

    return result.slice(startIndex, endIndex);
  });

  // Computed for total items
  totalItems = computed(() => {
    let result = [...this.data()];

    // Apply filters
    const activeFilters = this.filters().filter(
      (f) => f.value !== null && f.value !== undefined && f.value !== ''
    );
    if (activeFilters.length > 0) {
      result = result.filter((item) => {
        return activeFilters.every((filter) => {
          const itemValue = this.getNestedValue(item, filter.key);
          return this.matchesFilter(itemValue, filter.value, filter.operator || 'contains');
        });
      });
    }

    return result.length;
  });

  hasActiveFilters = computed(() => {
    return this.filters().some((f) => f.value !== null && f.value !== undefined && f.value !== '');
  });

  // Methods
  dataGridClasses = () => {
    const classes = ['os-data-grid'];

    if (this.size() !== 'medium') {
      classes.push(`os-data-grid--${this.size()}`);
    }

    if (this.variant() !== 'default') {
      classes.push(`os-data-grid--${this.variant()}`);
    }

    if (this.isLoading()) {
      classes.push('os-data-grid--loading');
    }

    return classes.join(' ');
  };

  getButtonSize = () => {
    const sizeMap: Record<OsDataGridSize, 'small' | 'medium' | 'large'> = {
      small: 'small',
      medium: 'medium',
      large: 'large',
    };
    return sizeMap[this.size()];
  };

  getFilterBarVariant = () => {
    const variantMap: Record<OsDataGridVariant, 'default' | 'compact' | 'expanded'> = {
      default: 'default',
      compact: 'compact',
      detailed: 'expanded',
    };
    return variantMap[this.variant()];
  };

  getFilterBarSize = () => {
    const sizeMap: Record<OsDataGridSize, 'small' | 'medium' | 'large'> = {
      small: 'small',
      medium: 'medium',
      large: 'large',
    };
    return sizeMap[this.size()];
  };

  getTableSize = () => {
    const sizeMap: Record<OsDataGridSize, 'small' | 'medium' | 'large'> = {
      small: 'small',
      medium: 'medium',
      large: 'large',
    };
    return sizeMap[this.size()];
  };

  getTableVariant = () => {
    const variantMap: Record<OsDataGridVariant, 'default' | 'striped' | 'bordered'> = {
      default: 'default',
      compact: 'striped',
      detailed: 'bordered',
    };
    return variantMap[this.variant()];
  };

  getFilterValue = (key: string) => {
    const filter = this.filters().find((f) => f.key === key);
    return filter?.value || '';
  };

  getItemCountText = () => {
    const total = this.totalItems();
    const start = this.pagination().page * this.pagination().pageSize + 1;
    const end = Math.min(start + this.pagination().pageSize - 1, total);

    if (total === 0) {
      return 'Nenhum item encontrado';
    }

    return `Mostrando ${start}-${end} de ${total} itens`;
  };

  // Event handlers
  onRefresh(): void {
    this.refresh.emit();
  }

  onExport(): void {
    this.export.emit();
  }

  onAdd(): void {
    this.add.emit();
  }

  onRowClick(row: Record<string, unknown>): void {
    this.rowClick.emit(row);
  }

  onTableActionClick(action: OsDataTableAction): void {
    this.tableActionClick.emit(action);
  }

  onFilterChange(key: string, event: Event | { target?: { value: unknown } } | unknown): void {
    const value = (event as { target?: { value: unknown } })?.target?.value || event;
    this.updateFilter(key, value as string | number | Date | boolean | null | undefined);
  }

  onClearFilters(): void {
    this.filters.set([]);
    this.filterChange.emit([]);
  }

  onApplyFilters(): void {
    this.filterChange.emit([...this.filters()]);
  }

  onSortChange(sort: { active: string; direction: 'asc' | 'desc' | '' }): void {
    if (sort.direction === '') return;

    this.sort.set({
      column: sort.active,
      direction: sort.direction as 'asc' | 'desc',
    });
    this.sortChange.emit(this.sort()!);
  }

  onPageChange(event: { pageIndex: number; pageSize: number }): void {
    this.pagination.update((p) => ({
      ...p,
      page: event.pageIndex,
      pageSize: event.pageSize,
    }));
    this.pageChange.emit(this.pagination());
  }

  // Helper methods
  private updateFilter(
    key: string,
    value: string | number | Date | boolean | null | undefined
  ): void {
    const currentFilters = [...this.filters()];
    const existingIndex = currentFilters.findIndex((f) => f.key === key);

    if (value === null || value === undefined || value === '') {
      if (existingIndex >= 0) {
        currentFilters.splice(existingIndex, 1);
      }
    } else {
      const filter: OsDataGridFilter = { key, value };
      if (existingIndex >= 0) {
        currentFilters[existingIndex] = filter;
      } else {
        currentFilters.push(filter);
      }
    }

    this.filters.set(currentFilters);
  }

  private getNestedValue(obj: Record<string, unknown>, path: string): unknown {
    return path
      .split('.')
      .reduce((current: unknown, key: string) => (current as Record<string, unknown>)?.[key], obj);
  }

  private matchesFilter(itemValue: unknown, filterValue: unknown, operator: string): boolean {
    if (itemValue === null || itemValue === undefined) {
      return false;
    }

    const itemStr = String(itemValue).toLowerCase();
    const filterStr = String(filterValue).toLowerCase();

    switch (operator) {
      case 'equals':
        return itemStr === filterStr;
      case 'contains':
        return itemStr.includes(filterStr);
      case 'startsWith':
        return itemStr.startsWith(filterStr);
      case 'endsWith':
        return itemStr.endsWith(filterStr);
      case 'gt':
        return Number(itemValue) > Number(filterValue);
      case 'lt':
        return Number(itemValue) < Number(filterValue);
      case 'gte':
        return Number(itemValue) >= Number(filterValue);
      case 'lte':
        return Number(itemValue) <= Number(filterValue);
      default:
        return itemStr.includes(filterStr);
    }
  }

  private applySorting(
    data: Record<string, unknown>[],
    sort: OsDataGridSort
  ): Record<string, unknown>[] {
    return [...data].sort((a, b) => {
      const aValue = this.getNestedValue(a, sort.column);
      const bValue = this.getNestedValue(b, sort.column);

      if (aValue === bValue) return 0;

      const aStr = String(aValue || '');
      const bStr = String(bValue || '');
      const comparison = aStr < bStr ? -1 : aStr > bStr ? 1 : 0;
      return sort.direction === 'asc' ? comparison : -comparison;
    });
  }
}

