        event.preventDefault();
        this._searchQuery.set('');
        this._isOpen.set(false);
        this.triggerElement?.nativeElement?.focus();
        break;
      case 'ArrowDown':
        event.preventDefault();
        this.focusNextOption();
        break;
      case 'ArrowUp':
        event.preventDefault();
        this.focusPreviousOption();
        break;
    }
