        if (group.options.length > 0) {
          result.push({ value: '', label: group.name, isGroupHeader: true, disabled: true });
          result.push(...group.options);
        }
      });
    } else {
      result.push(...optionsList);
    }

    return result;
  });

  filteredOptions = computed(() => {
    const query = this._searchQuery().toLowerCase().trim();
    if (!query) {
      return this.flattenedOptions();
    }

    return this.flattenedOptions().filter((option) => {
      if (option.isGroupHeader || option.divider) {
        return false;
      }
      return option.label.toLowerCase().includes(query);
    });
  });

  virtualScrollItems = computed(() => {
    return this.filteredOptions();
  });

  getIconSize = () => {
    const sizeMap: Record<OsDropdownSize, 'sm' | 'md' | 'lg'> = {
      small: 'sm',
      medium: 'md',
      large: 'lg',
    };
    return sizeMap[this.size()];
  };

  getIconVariant = () => {
    const variantMap: Record<OsDropdownVariant, 'default' | 'primary' | 'secondary' | 'info'> = {
      default: 'default',
      primary: 'primary',
      secondary: 'secondary',
      accent: 'info',
    };
    return variantMap[this.variant()];
  };

  getItemHeight = () => {
    const heightMap: Record<OsDropdownSize, number> = {
      small: 32,
      medium: 40,
      large: 48,
    };
    return heightMap[this.size()];
  };

  containerClass = computed(() => {
    const classes = ['os-dropdown'];

    if (this.variant() !== 'default') {
      classes.push(`os-dropdown--${this.variant()}`);
    }

    if (this.size() !== 'medium') {
      classes.push(`os-dropdown--${this.size()}`);
    }

    if (this.disabled()) {
      classes.push('os-dropdown--disabled');
    }

    if (this.isMobile()) {
      classes.push('os-dropdown--mobile');
    }

    return classes.join(' ');
  });

  triggerClass = computed(() => {
    const classes = ['os-dropdown__trigger'];

    if (this.variant() !== 'default') {
      classes.push(`os-dropdown__trigger--${this.variant()}`);
    }

    if (this.size() !== 'medium') {
      classes.push(`os-dropdown__trigger--${this.size()}`);
    }

    if (this.isOpen()) {
      classes.push('os-dropdown__trigger--open');
    }

    return classes.join(' ');
  });

  textClass = computed(() => {
    const classes = ['os-dropdown__text'];

    if (this.size() !== 'medium') {
      classes.push(`os-dropdown__text--${this.size()}`);
    }

    return classes.join(' ');
  });

  menuClass = computed(() => {
    const classes = ['os-dropdown__menu'];

    if (this.size() !== 'medium') {
      classes.push(`os-dropdown__menu--${this.size()}`);
    }

    if (this.isMobile()) {
      classes.push('os-dropdown__menu--mobile');
    }

    return classes.join(' ');
  });

  placeholderClass = computed(() => {
    const classes = ['os-dropdown__placeholder'];

    if (this.size() !== 'medium') {
      classes.push(`os-dropdown__placeholder--${this.size()}`);
    }

    return classes.join(' ');
  });

  virtualScrollClass = computed(() => {
    const classes = ['os-dropdown__virtual-scroll'];

    if (this.size() !== 'medium') {
      classes.push(`os-dropdown__virtual-scroll--${this.size()}`);
    }

    return classes.join(' ');
  });

  groupHeaderClass = computed(() => {
    const classes = ['os-dropdown__group-header'];

    if (this.size() !== 'medium') {
      classes.push(`os-dropdown__group-header--${this.size()}`);
    }

    return classes.join(' ');
  });

  emptyStateClass = computed(() => {
    const classes = ['os-dropdown__empty-state'];

    if (this.size() !== 'medium') {
      classes.push(`os-dropdown__empty-state--${this.size()}`);
    }

    return classes.join(' ');
  });

  optionClass = (option: OsDropdownOption) => {
    const classes = ['os-dropdown__option'];

    if (this.size() !== 'medium') {
      classes.push(`os-dropdown__option--${this.size()}`);
    }

    if (option.disabled) {
      classes.push('os-dropdown__option--disabled');
    }

    if (option.value === this.selectedValue()) {
      classes.push('os-dropdown__option--selected');
    }

    return classes.join(' ');
  };

  optionTextClass = computed(() => {
    const classes = ['os-dropdown__option-text'];

    if (this.size() !== 'medium') {
      classes.push(`os-dropdown__option-text--${this.size()}`);
    }

    return classes.join(' ');
  });

  xPosition = computed(() => {
    switch (this.alignment()) {
      case 'start':
        return 'before';
      case 'end':
        return 'after';
      case 'center':
      default:
        return 'after';
    }
  });

  yPosition = computed(() => 'below' as const);

  handleTriggerClick(): void {
    if (!this.disabled()) {
      this._isOpen.set(true);
    }
  }

  handleMenuOpened(): void {
    this._isOpen.set(true);
    this._searchQuery.set('');
    this._focusedOptionIndex.set(-1);
    this.menuOpen.emit();

    if (this.searchable() && this.filteredOptions().length > this.searchThreshold()) {
      setTimeout(() => {
        this.searchInputElement?.nativeElement?.querySelector('input')?.focus();
      }, 100);
    }
  }

  handleMenuClosed(): void {
    this._isOpen.set(false);
    this._searchQuery.set('');
    this._focusedOptionIndex.set(-1);
    this.menuClose.emit();
  }

  handleOptionClick(option: OsDropdownOption): void {
    if (!option.disabled) {
      this.valueChange.emit(option.value);
      this.optionSelect.emit(option);
      this._isOpen.set(false);
    }
  }

  handleSearchChange(value: string): void {
    this._searchQuery.set(value);
    this.searchChange.emit(value);
  }

  handleTriggerKeydown(event: KeyboardEvent): void {
    if (this.disabled()) {
      return;
    }

    switch (event.key) {
      case 'ArrowDown':
      case 'ArrowUp':
        event.preventDefault();
        if (!this.isOpen()) {
          this._isOpen.set(true);
        }
        break;
      case 'Enter':
      case ' ':
        event.preventDefault();
        this._isOpen.set(!this.isOpen());
        break;
      case 'Escape':
        event.preventDefault();
        this._isOpen.set(false);
        break;
    }
  }

  handleOptionKeydown(event: KeyboardEvent, option: OsDropdownOption): void {
    switch (event.key) {
      case 'Enter':
      case ' ':
        event.preventDefault();
        this.handleOptionClick(option);
        break;
      case 'Escape':
        event.preventDefault();
        this._isOpen.set(false);
        this.triggerElement?.nativeElement?.focus();
        break;
      case 'ArrowDown':
        event.preventDefault();
        this.focusNextOption();
        break;
      case 'ArrowUp':
        event.preventDefault();
        this.focusPreviousOption();
        break;
    }
  }

  handleSearchKeydown(event: KeyboardEvent): void {
    switch (event.key) {
      case 'Escape':
        event.preventDefault();
        this._searchQuery.set('');
        this._isOpen.set(false);
        this.triggerElement?.nativeElement?.focus();
        break;
      case 'ArrowDown':
        event.preventDefault();
        this.focusNextOption();
        break;
      case 'ArrowUp':
        event.preventDefault();
        this.focusPreviousOption();
        break;
    }
  }

  @HostListener('document:keydown', ['$event'])
  handleDocumentKeydown(event: KeyboardEvent): void {
    if (!this.isOpen()) {
      return;
    }

    if (event.key === 'Escape') {
      event.preventDefault();
      this._isOpen.set(false);
      this.triggerElement?.nativeElement?.focus();
    }
  }

  private focusNextOption(): void {
    const options = this.filteredOptions().filter((opt) => !opt.disabled && !opt.isGroupHeader);
    if (options.length === 0) {
      return;
    }

    const currentIndex = this._focusedOptionIndex();
    const nextIndex = (currentIndex + 1) % options.length;
    this._focusedOptionIndex.set(nextIndex);
  }

  private focusPreviousOption(): void {
    const options = this.filteredOptions().filter((opt) => !opt.disabled && !opt.isGroupHeader);
    if (options.length === 0) {
      return;
    }

    const currentIndex = this._focusedOptionIndex();
    const previousIndex = currentIndex <= 0 ? options.length - 1 : currentIndex - 1;
    this._focusedOptionIndex.set(previousIndex);
  }

  private getAllOptions(): OsDropdownOption[] {
    const groupsList = this.groups();
    const optionsList = this.options();

    if (groupsList.length > 0) {
      return groupsList.flatMap((group) => group.options);
    }

    return optionsList;
  }
}

import {
  Component,
  input,
  output,
  computed,
  effect,
  signal,
  ChangeDetectionStrategy,
  inject,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { OsButtonComponent } from '../../atoms/os-button/os-button.component';

export type OsFilterBarVariant = 'default' | 'compact' | 'expanded';
export type OsFilterBarSize = 'small' | 'medium' | 'large';

export interface OsFilterOption {
  key: string;
  label: string;
  value: string | number | Date | null;
  type: 'text' | 'select' | 'date' | 'number';
  options?: { value: string | number; label: string }[];
}

/**
 * OsFilterBarComponent
 *
 * Barra de filtros responsiva com suporte a persistncia em localStorage,
 * acessibilidade WCAG 2.1 AA e touch targets >= 44px para mobile.
 *
 * @example
 * ```html
 * <os-filter-bar
 *   [persistKey]="'budgets-filters'"
 *   [hasActiveFilters]="hasFilters"
 *   (clear)="onClearFilters()"
 *   (apply)="onApplyFilters()">
 *   <!-- Filter inputs here -->
 * </os-filter-bar>
 * ```
 */
@Component({
  selector: 'os-filter-bar',
  standalone: true,
  imports: [CommonModule, OsButtonComponent],
  template: `
    <div
      class="os-filter-bar"
      [class]="filterBarClasses()"
      role="search"
      [attr.aria-label]="ariaLabel()"
      [attr.aria-describedby]="ariaDescribedBy() || null"
    >
      <div class="os-filter-bar__content">
        <ng-content></ng-content>
      </div>

      @if (showActions()) {
      <div class="os-filter-bar__actions">
        @if (showClearButton()) {
        <os-button
          variant="tertiary"
          [size]="getButtonSize()"
          icon="clear"
          [disabled]="!hasActiveFilters()"
          [ariaLabel]="clearButtonAriaLabel()"
          (buttonClick)="onClear()"
        >
          {{ clearButtonText() }}
        </os-button>
        } @if (showApplyButton()) {
        <os-button
          variant="primary"
          [size]="getButtonSize()"
          icon="check"
          [disabled]="!hasActiveFilters()"
          [ariaLabel]="applyButtonAriaLabel()"
          (buttonClick)="onApply()"
        >
          {{ applyButtonText() }}
        </os-button>
        }
      </div>
      }
    </div>
  `,
  styleUrl: './os-filter-bar.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush,
  host: {
    class: 'os-filter-bar-host',
    '[attr.data-variant]': 'variant()',
    '[attr.data-size]': 'size()',
  },
})
export class OsFilterBarComponent {
  // --- INPUTS ---
  variant = input<OsFilterBarVariant>('default');
  size = input<OsFilterBarSize>('medium');
  showActions = input<boolean>(true);
  showClearButton = input<boolean>(true);
  showApplyButton = input<boolean>(true);
  clearButtonText = input<string>('Limpar');
  applyButtonText = input<string>('Aplicar');
  hasActiveFilters = input<boolean>(false);

  // Persistncia em localStorage
  persistKey = input<string | null>(null);
  persistFilters = input<boolean>(false);

  // Acessibilidade
  ariaLabel = input<string>('Barra de filtros');
  ariaDescribedBy = input<string>('');
  clearButtonAriaLabel = input<string>('Limpar todos os filtros');
  applyButtonAriaLabel = input<string>('Aplicar filtros');

  // --- OUTPUTS ---
  clear = output<void>();
  apply = output<void>();
  filtersRestored = output<Record<string, unknown>>();

  // --- SIGNALS INTERNOS ---
  private isMobile = signal<boolean>(false);

  // --- COMPUTED PROPERTIES ---
  filterBarClasses = computed(() => {
    const classes = ['os-filter-bar'];

    if (this.variant() !== 'default') {
      classes.push(`os-filter-bar--${this.variant()}`);
    }

    if (this.size() !== 'medium') {
      classes.push(`os-filter-bar--${this.size()}`);
    }

    if (this.isMobile()) {
      classes.push('os-filter-bar--mobile');
    }

    return classes.join(' ');
  });

  // --- CONSTRUCTOR ---
  constructor() {
    // Effect para detectar mobile
    effect(
      () => {
        if (typeof window !== 'undefined') {
          const checkMobile = () => {
            this.isMobile.set(window.innerWidth < 768);
          };
          checkMobile();
          window.addEventListener('resize', checkMobile);
        }
      },
      { allowSignalWrites: true }
    );

    // Effect para restaurar filtros persistidos
    effect(
      () => {
        if (this.persistFilters() && this.persistKey()) {
          this.restoreFilters();
        }
      },
      { allowSignalWrites: true }
    );
  }

  // --- MTODOS PBLICOS ---

  /**
   * Retorna o tamanho do boto baseado no tamanho da filter bar
   */
  getButtonSize(): 'small' | 'medium' | 'large' {
    const sizeMap: Record<OsFilterBarSize, 'small' | 'medium' | 'large'> = {
      small: 'small',
      medium: 'medium',
      large: 'large',
    };
    return sizeMap[this.size()];
  }

  /**
   * Handler para evento de limpar filtros
   */
  onClear(): void {
    if (this.persistFilters() && this.persistKey()) {
      this.clearPersistedFilters();
    }
    this.clear.emit();
  }

  /**
   * Handler para evento de aplicar filtros
   */
  onApply(): void {
    this.apply.emit();
  }

  /**
   * Persiste os filtros atuais no localStorage
   */
  saveFilters(filters: Record<string, unknown>): void {
    if (!this.persistFilters() || !this.persistKey() || typeof window === 'undefined') {
      return;
    }

    try {
      const key = this.getStorageKey();
      localStorage.setItem(key, JSON.stringify(filters));
    } catch (error) {
      console.warn('Falha ao persistir filtros:', error);
    }
  }

  /**
   * Restaura os filtros do localStorage
   */
  restoreFilters(): void {
    if (!this.persistFilters() || !this.persistKey() || typeof window === 'undefined') {
      return;
    }

    try {
      const key = this.getStorageKey();
      const stored = localStorage.getItem(key);

      if (stored) {
        const filters = JSON.parse(stored) as Record<string, unknown>;
        this.filtersRestored.emit(filters);
      }
    } catch (error) {
      console.warn('Falha ao restaurar filtros:', error);
    }
  }

  /**
   * Limpa os filtros persistidos do localStorage
   */
  clearPersistedFilters(): void {
    if (!this.persistKey() || typeof window === 'undefined') {
      return;
    }

    try {
      const key = this.getStorageKey();
      localStorage.removeItem(key);
    } catch (error) {
      console.warn('Falha ao limpar filtros persistidos:', error);
    }
  }

  // --- MTODOS PRIVADOS ---

  /**
   * Retorna a chave completa para o localStorage
   */
  private getStorageKey(): string {
    return `os-filter-bar:${this.persistKey()}`;
  }
}

import {
  Component,
  input,
  output,
  computed,
  ChangeDetectionStrategy,
  inject,
  ElementRef,
  HostListener,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatDialogModule, MatDialogRef } from '@angular/material/dialog';
import { OsButtonComponent } from '../../atoms/os-button/os-button.component';
import { OsCardComponent } from '../../molecules/os-card/os-card.component';

export type OsModalVariant = 'default' | 'confirmation' | 'form' | 'info';
export type OsModalSize = 'small' | 'medium' | 'large' | 'fullscreen';

export interface OsModalAction {
  label: string;
  variant: 'primary' | 'secondary' | 'tertiary' | 'danger';
  action: () => void;
  disabled?: boolean;
  loading?: boolean;
}

@Component({
  selector: 'os-modal',
  standalone: true,
  imports: [CommonModule, MatDialogModule, OsButtonComponent, OsCardComponent],
  template: `
    <div
      class="os-modal"
      [class]="modalClasses()"
      [attr.role]="'dialog'"
      [attr.aria-modal]="true"
      [attr.aria-labelledby]="titleId()"
      [attr.aria-describedby]="descriptionId()"
    >
      <div
        class="os-modal__backdrop"
        (click)="onBackdropClick()"
        (keydown.enter)="onBackdropClick()"
        tabindex="0"
        role="button"
        [attr.aria-label]="'Fechar modal clicando no fundo'"
      ></div>

      <div class="os-modal__container" [class]="containerClasses()">
        <os-card
          [variant]="cardVariant()"
          [size]="cardSize()"
          [header]="showHeader()"
          [actions]="showActions()"
        >
          @if (showHeader()) {
          <div slot="header" class="os-modal__header">
            @if (title()) {
            <h2 [id]="titleId()" class="os-modal__title">{{ title() }}</h2>
            } @if (subtitle()) {
            <p [id]="descriptionId()" class="os-modal__subtitle">{{ subtitle() }}</p>
            } @if (closable()) {
            <button
              class="os-modal__close"
              (click)="onClose()"
              [attr.aria-label]="'Fechar modal'"
              type="button"
            >
              <span class="os-modal__close-icon" aria-hidden="true"></span>
            </button>
            }
          </div>
          }

          <div class="os-modal__content" [class]="contentClasses()">
            <ng-content></ng-content>
          </div>

          @if (showActions()) {
          <div slot="actions" class="os-modal__actions" [class]="actionsClasses()">
            @if (actions().length > 0) { @for (action of actions(); track action.label) {
            <os-button
              [variant]="action.variant"
              [size]="buttonSize()"
              [disabled]="action.disabled || false"
              [loading]="action.loading || false"
              (clicked)="action.action()"
            >
              {{ action.label }}
            </os-button>
            } } @else { @if (showDefaultActions()) {
            <os-button variant="secondary" [size]="buttonSize()" (clicked)="onClose()">
              {{ cancelText() }}
            </os-button>
            <os-button
              variant="primary"
              [size]="buttonSize()"
              [disabled]="confirmDisabled()"
              [loading]="confirmLoading()"
              (clicked)="onConfirm()"
            >
              {{ confirmText() }}
            </os-button>
            } }
          </div>
          }
        </os-card>
      </div>
    </div>
  `,
  styleUrls: ['./os-modal.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  host: {
    class: 'os-modal-host',
  },
})
export class OsModalComponent {
  // Input signals
  readonly title = input<string | null>(null);
  readonly subtitle = input<string | null>(null);
  readonly variant = input<OsModalVariant>('default');
  readonly size = input<OsModalSize>('medium');
  readonly closable = input<boolean>(true);
  readonly closeOnBackdrop = input<boolean>(true);
  readonly closeOnEscape = input<boolean>(true);
  readonly actions = input<OsModalAction[]>([]);
  readonly showDefaultActions = input<boolean>(false);
  readonly confirmText = input<string>('Confirmar');
  readonly cancelText = input<string>('Cancelar');
  readonly confirmDisabled = input<boolean>(false);
  readonly confirmLoading = input<boolean>(false);
  readonly fullHeight = input<boolean>(false);
  readonly centered = input<boolean>(true);

  // Output signals
  readonly closed = output<void>();
  readonly confirmed = output<void>();
  readonly backdropClicked = output<void>();

  // Private properties
  private readonly elementRef = inject(ElementRef);
  private readonly dialogRef = inject(MatDialogRef<OsModalComponent>, { optional: true });

  // Computed values
  readonly titleId = computed(() => `modal-title-${Math.random().toString(36).substr(2, 9)}`);
  readonly descriptionId = computed(
    () => `modal-description-${Math.random().toString(36).substr(2, 9)}`
  );

  readonly showHeader = computed(() => {
    return this.title() !== null || this.subtitle() !== null || this.closable();
  });

  readonly showActions = computed(() => {
    return this.actions().length > 0 || this.showDefaultActions();
  });

  readonly cardVariant = computed(() => {
    const variant = this.variant();
    switch (variant) {
      case 'confirmation':
        return 'outlined';
      case 'form':
        return 'elevated';
      case 'info':
        return 'flat';
      default:
        return 'default';
    }
  });

  readonly cardSize = computed(() => {
    const size = this.size();
    switch (size) {
      case 'small':
        return 'small';
      case 'large':
      case 'fullscreen':
        return 'large';
      default:
        return 'medium';
    }
  });

  readonly buttonSize = computed(() => {
    const size = this.size();
    switch (size) {
      case 'small':
        return 'small';
      case 'large':
      case 'fullscreen':
        return 'large';
      default:
        return 'medium';
    }
  });

  readonly modalClasses = computed(() => {
    const classes = ['os-modal'];
    classes.push(`os-modal--${this.variant()}`);
    classes.push(`os-modal--${this.size()}`);

    if (this.fullHeight()) {
      classes.push('os-modal--full-height');
    }

    if (this.centered()) {
      classes.push('os-modal--centered');
    }

    return classes.join(' ');
  });

  readonly containerClasses = computed(() => {
    const classes = ['os-modal__container'];
    classes.push(`os-modal__container--${this.size()}`);

    if (this.centered()) {
      classes.push('os-modal__container--centered');
    }

    return classes.join(' ');
  });

  readonly contentClasses = computed(() => {
    const classes = ['os-modal__content'];

    if (this.fullHeight()) {
      classes.push('os-modal__content--full-height');
    }

    return classes.join(' ');
  });

  readonly actionsClasses = computed(() => {
    const classes = ['os-modal__actions'];
    const actionCount = this.actions().length || (this.showDefaultActions() ? 2 : 0);

    if (actionCount > 2) {
      classes.push('os-modal__actions--multiple');
    } else {
      classes.push('os-modal__actions--dual');
    }

    return classes.join(' ');
  });

  // Event handlers
  onClose(): void {
    this.closed.emit();
    if (this.dialogRef) {
      this.dialogRef.close();
    }
  }

  onConfirm(): void {
    this.confirmed.emit();
  }

  onBackdropClick(): void {
    if (this.closeOnBackdrop()) {
      this.backdropClicked.emit();
      this.onClose();
    }
  }

  @HostListener('document:keydown.escape', ['$event'])
  onEscapeKey(event: Event): void {
    const keyboardEvent = event as KeyboardEvent;
    if (this.closeOnEscape()) {
      keyboardEvent.preventDefault();
      this.onClose();
    }
  }

  @HostListener('document:keydown.enter', ['$event'])
  onEnterKey(event: Event): void {
    const keyboardEvent = event as KeyboardEvent;
    if (keyboardEvent.ctrlKey || keyboardEvent.metaKey) {
      keyboardEvent.preventDefault();
      this.onConfirm();
    }
  }
}

import { CommonModule } from '@angular/common';
import {
  ChangeDetectionStrategy,
  Component,
  computed,
  input,
  output,
  signal,
  effect,
  inject,
} from '@angular/core';
import { debounceTime, distinctUntilChanged, Subject } from 'rxjs';

import { OsButtonComponent } from '../../atoms/os-button/os-button.component';
import { OsInputComponent } from '../../atoms/os-input/os-input.component';

export type OsSearchBoxSize = 'small' | 'medium' | 'large';
export type OsSearchBoxVariant = 'default' | 'outlined' | 'filled';
export type OsSearchBoxRole = 'searchbox' | 'combobox' | 'textbox';

export interface OsSearchSuggestion {
  id: string;
  text: string;
  category?: string;
  highlightedText?: string;
}

@Component({
  selector: 'os-search-box',
  standalone: true,
  imports: [CommonModule, OsInputComponent, OsButtonComponent],
  template: `
    <div
      class="os-search-box"
      [class]="searchBoxClasses()"
      [attr.role]="role()"
      [attr.aria-controls]="showSuggestions() ? suggestionsListboxId() : null"
      [attr.aria-expanded]="showSuggestions() && suggestions().length > 0"
      [attr.aria-haspopup]="showSuggestions() ? 'listbox' : null"
      [attr.aria-activedescendant]="activeSuggestionId()"
      [attr.aria-describedby]="ariaDescribedBy() || null"
    >
      <os-input
        [value]="value()"
        [placeholder]="placeholder()"
        [disabled]="disabled()"
        [size]="inputSize()"
        [prefixIcon]="'search'"
        [clearable]="true"
        [ariaLabel]="ariaLabel()"
        (valueChange)="onValueChange($event)"
        (focusEvent)="onFocus($event)"
        (blurEvent)="onBlur($event)"
        (keydown)="onKeydown($event)"
      />

      @if (showSuggestions() && filteredSuggestions().length > 0) {
      <div
        class="os-search-box__suggestions"
        [class]="suggestionsClass()"
        role="listbox"
        [attr.id]="suggestionsListboxId()"
        [attr.aria-label]="'Sugestes de busca'"
      >
        @for (suggestion of filteredSuggestions(); track suggestion.id; let i = $index) {
        <os-button
          variant="tertiary"
          size="small"
          [class]="suggestionClass()"
          [class.os-search-box__suggestion--active]="i === activeSuggestionIndex()"
          (buttonClick)="onSuggestionClick(suggestion)"
          (mouseenter)="onSuggestionHover(suggestion, i)"
          [attr.aria-label]="'Selecionar: ' + suggestion.text"
          [attr.aria-posinset]="i + 1"
          [attr.aria-setsize]="filteredSuggestions().length"
          [attr.id]="'suggestion-' + suggestion.id"
        >
          @if (suggestion.category) {
          <span class="os-search-box__suggestion-category">{{ suggestion.category }}</span>
          }
          <span
            class="os-search-box__suggestion-text"
            [innerHTML]="suggestion.highlightedText || suggestion.text"
          ></span>
        </os-button>
        }
      </div>
      } @if (showSuggestions() && suggestions().length > 0 && filteredSuggestions().length === 0) {
      <div class="os-search-box__no-results" role="status" [attr.aria-live]="'polite'">
        <span class="os-search-box__no-results-text">Nenhuma sugesto encontrada</span>
      </div>
      }
    </div>
  `,
  styleUrl: './os-search-box.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush,
  host: {
    class: 'os-search-box-host',
  },
})
export class OsSearchBoxComponent {
  // Inputs
  value = input<string>('');
  placeholder = input<string>('Buscar...');
  disabled = input<boolean>(false);
  size = input<OsSearchBoxSize>('medium');
  variant = input<OsSearchBoxVariant>('default');
  showSuggestions = input<boolean>(true);
  suggestions = input<OsSearchSuggestion[]>([]);
  maxSuggestions = input<number>(5);
  debounceTime = input<number>(300);
  role = input<OsSearchBoxRole>('searchbox');
  ariaLabel = input<string>('');
  ariaDescribedBy = input<string>('');
  enableHighlight = input<boolean>(true);
  enableKeyboardNavigation = input<boolean>(true);
  enableAdvancedFilters = input<boolean>(false);

  // Outputs
  valueChange = output<string>();
  suggestionSelect = output<OsSearchSuggestion>();
  searchEvent = output<string>();
  focusEvent = output<FocusEvent>();
  blurEvent = output<FocusEvent>();
  debouncedSearch = output<string>();
  suggestionHover = output<OsSearchSuggestion>();

  // Internal signals
  private searchSubject = new Subject<string>();
  protected activeSuggestionIndex = signal<number>(-1);
  private isFocused = signal<boolean>(false);
  private searchHistory = signal<string[]>([]);
  private static nextId = 0;
  private readonly instanceId = OsSearchBoxComponent.nextId++;

  constructor() {
    // Setup debounced search
    this.searchSubject
      .pipe(debounceTime(this.debounceTime()), distinctUntilChanged())
      .subscribe((value) => {
        this.debouncedSearch.emit(value);
      });
  }

  // Computed properties
  protected inputSize = computed(() => {
    const sizeMap: Record<OsSearchBoxSize, 'small' | 'medium' | 'large'> = {
      small: 'small',
      medium: 'medium',
      large: 'large',
    };
    return sizeMap[this.size()];
  });

  filteredSuggestions = computed(() => {
    const suggestions = this.suggestions();
    const value = this.value().toLowerCase();
    const maxSuggestions = this.maxSuggestions();

    if (!value || !this.enableHighlight()) {
      return suggestions.slice(0, maxSuggestions);
    }

    const filtered = suggestions
      .filter(
        (suggestion) =>
          suggestion.text.toLowerCase().includes(value) ||
          (suggestion.category && suggestion.category.toLowerCase().includes(value))
      )
      .map((suggestion) => ({
        ...suggestion,
        highlightedText: this.enableHighlight()
          ? this.highlightText(suggestion.text, value)
          : suggestion.text,
      }))
      .slice(0, maxSuggestions);

    return filtered;
  });

  suggestionsListboxId = computed(() => `os-search-box-suggestions-${this.instanceId}`);

  activeSuggestionId = computed(() => {
    const index = this.activeSuggestionIndex();
    const suggestions = this.filteredSuggestions();

    if (index >= 0 && index < suggestions.length) {
      return `suggestion-${suggestions[index].id}`;
    }

    return null;
  });

  searchBoxClasses = computed(() => {
    const classes = ['os-search-box'];

    if (this.size() !== 'medium') {
      classes.push(`os-search-box--${this.size()}`);
    }

    if (this.variant() !== 'default') {
      classes.push(`os-search-box--${this.variant()}`);
    }

    if (this.isFocused()) {
      classes.push('os-search-box--focused');
    }

    if (this.disabled()) {
      classes.push('os-search-box--disabled');
    }

    return classes.join(' ');
  });

  suggestionsClass = computed(() => {
    const classes = ['os-search-box__suggestions'];

    if (this.size() !== 'medium') {
      classes.push(`os-search-box__suggestions--${this.size()}`);
    }

    if (this.filteredSuggestions().length === 0) {
      classes.push('os-search-box__suggestions--empty');
    }

    return classes.join(' ');
  });

  suggestionClass = computed(() => {
    const classes = ['os-search-box__suggestion'];

    if (this.size() !== 'medium') {
      classes.push(`os-search-box__suggestion--${this.size()}`);
    }

    return classes.join(' ');
  });

  // Event handlers
  onValueChange(value: string): void {
    this.valueChange.emit(value);
    this.searchSubject.next(value);
    this.resetActiveSuggestion();
  }

  onFocus(event: FocusEvent): void {
    this.isFocused.set(true);
    this.focusEvent.emit(event);
  }

  onBlur(event: FocusEvent): void {
    this.isFocused.set(false);
    this.blurEvent.emit(event);
    this.resetActiveSuggestion();
  }

  onKeydown(event: KeyboardEvent): void {
    if (!this.enableKeyboardNavigation()) return;

    const suggestions = this.filteredSuggestions();
    const currentIndex = this.activeSuggestionIndex();

    switch (event.key) {
      case 'Enter':
        event.preventDefault();
        if (currentIndex >= 0 && currentIndex < suggestions.length) {
          this.selectSuggestion(suggestions[currentIndex]);
        } else {
          this.searchEvent.emit(this.value());
        }
        break;

      case 'ArrowDown':
        event.preventDefault();
        if (suggestions.length > 0) {
          const nextIndex = currentIndex < suggestions.length - 1 ? currentIndex + 1 : 0;
          this.activeSuggestionIndex.set(nextIndex);
        }
        break;

      case 'ArrowUp':
        event.preventDefault();
        if (suggestions.length > 0) {
          const prevIndex = currentIndex > 0 ? currentIndex - 1 : suggestions.length - 1;
          this.activeSuggestionIndex.set(prevIndex);
        }
        break;

      case 'Escape':
        event.preventDefault();
        this.resetActiveSuggestion();
        break;

      case 'Tab':
        if (currentIndex >= 0 && currentIndex < suggestions.length) {
          this.selectSuggestion(suggestions[currentIndex]);
        }
        break;
    }
  }

  onSuggestionClick(suggestion: OsSearchSuggestion): void {
    this.selectSuggestion(suggestion);
  }

  onSuggestionHover(suggestion: OsSearchSuggestion, index: number): void {
    this.activeSuggestionIndex.set(index);
    this.suggestionHover.emit(suggestion);
  }

  // Helper methods
  private selectSuggestion(suggestion: OsSearchSuggestion): void {
    this.suggestionSelect.emit(suggestion);
    this.valueChange.emit(suggestion.text);
    this.resetActiveSuggestion();
    this.addToSearchHistory(suggestion.text);
  }

  private resetActiveSuggestion(): void {
    this.activeSuggestionIndex.set(-1);
  }

  private highlightText(text: string, searchTerm: string): string {
    if (!searchTerm) return text;

    const regex = new RegExp(`(${searchTerm})`, 'gi');
    return text.replace(regex, '<mark class="os-search-box__highlight">$1</mark>');
  }

  private addToSearchHistory(searchTerm: string): void {
    if (!searchTerm.trim()) return;

    const history = this.searchHistory();
    const filtered = history.filter((term) => term !== searchTerm);
    const newHistory = [searchTerm, ...filtered].slice(0, 10); // Keep last 10 searches
    this.searchHistory.set(newHistory);
  }

  // Public methods for external access
  getSearchHistory(): string[] {
    return this.searchHistory();
  }

  clearSearchHistory(): void {
    this.searchHistory.set([]);
  }

  focus(): void {
    // This would need to be implemented with ViewChild to access the input element
    // For now, we'll emit a focus event
    this.isFocused.set(true);
  }

  blur(): void {
    this.isFocused.set(false);
  }
}

import {
  Component,
  input,
  output,
  computed,
  model,
  signal,
  ChangeDetectionStrategy,
  forwardRef,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { MatSelectModule } from '@angular/material/select';
import { MatFormFieldModule, MatFormFieldAppearance } from '@angular/material/form-field';

export type OsSelectSize = 'small' | 'medium' | 'large';

export interface OsSelectOption {
  value: string | number;
  label: string;
  disabled?: boolean;
}

@Component({
  selector: 'os-select',
  standalone: true,
  imports: [CommonModule, MatSelectModule, MatFormFieldModule],
  template: `
    <div [class]="containerClass()">
      <mat-form-field [appearance]="appearance()" [class]="formFieldClass()">
        @if (label()) {
        <mat-label>{{ label() }}</mat-label>
        }

        <mat-select
          [id]="selectId"
          [disabled]="disabled()"
          [required]="required()"
          [value]="value()"
          [placeholder]="placeholder()"
          [class]="selectClass()"
          [attr.aria-label]="ariaLabel() || label()"
          [attr.aria-describedby]="computedAriaDescribedBy()"
          [attr.aria-invalid]="hasError()"
          [attr.aria-required]="required() ? 'true' : 'false'"
          [attr.aria-disabled]="disabled() ? 'true' : 'false'"
          [attr.tabindex]="disabled() ? -1 : 0"
          (selectionChange)="handleChange($event)"
          (blur)="handleBlur($event)"
          (focus)="handleFocus($event)"
          (openedChange)="onOpenedChange($event)"
        >
          @for (option of options(); track option.value) {
          <mat-option [value]="option.value" [disabled]="option.disabled">
            {{ option.label }}
          </mat-option>
          }
        </mat-select>

        @if (helperText() || hasError()) {
        <mat-hint [id]="selectId + '-helper'" [class]="helperClass()">
          {{ errorMessage() || helperText() }}
        </mat-hint>
        }
      </mat-form-field>
    </div>
  `,
  styleUrls: ['./os-select.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => OsSelectComponent),
      multi: true,
    },
  ],
})
export class OsSelectComponent implements ControlValueAccessor {
  size = input<OsSelectSize>('medium');
  label = input<string>('');
  placeholder = input<string>('');
  helperText = input<string>('');
  errorMessage = input<string>('');
  disabled = model(false);
  required = input(false);
  value = model<string | number>('');
  options = input<OsSelectOption[]>([]);
  ariaLabel = input<string>('');
  ariaDescribedBy = input<string>('');
  animated = input(true);
  hapticFeedback = input(true);

  valueChange = output<string | number>();
  blurEvent = output<FocusEvent>();
  focusEvent = output<FocusEvent>();
  openedChange = output<boolean>();

  private _onChange = (value: string | number) => {
    // This will be set by registerOnChange
    console.debug('onChange called with:', value);
  };
  private _onTouched = () => {
    // This will be set by registerOnTouched
  };

  selectId = `os-select-${Math.random().toString(36).substr(2, 9)}`;
  isFocused = signal(false);
  isHovered = signal(false);
  isOpened = signal(false);

  containerClass = computed(() => {
    return [
      'os-select-container',
      `os-select-container--${this.size()}`,
      this.hasError() ? 'os-select-container--error' : '',
      this.disabled() ? 'os-select-container--disabled' : '',
      this.isFocused() ? 'os-select-container--focused' : '',
      this.isHovered() ? 'os-select-container--hovered' : '',
      this.isOpened() ? 'os-select-container--opened' : '',
      this.animated() ? 'os-select-container--animated' : '',
    ]
      .filter(Boolean)
      .join(' ');
  });

  labelClass = computed(() => {
    return ['os-select__label', this.required() ? 'os-select__label--required' : '']
      .filter(Boolean)
      .join(' ');
  });

  selectWrapperClass = computed(() => {
    return ['os-select__wrapper', this.disabled() ? 'os-select__wrapper--disabled' : '']
      .filter(Boolean)
      .join(' ');
  });

  selectClass = computed(() => {
    return [
      'os-select',
      `os-select--${this.size()}`,
      this.hasError() ? 'os-select--error' : '',
      this.disabled() ? 'os-select--disabled' : '',
    ]
      .filter(Boolean)
      .join(' ');
  });

  helperClass = computed(() => {
    return ['os-select__helper', this.hasError() ? 'os-select__helper--error' : '']
      .filter(Boolean)
      .join(' ');
  });

  hasError = computed(() => {
    return !!this.errorMessage();
  });

  computedAriaDescribedBy = computed(() => {
    const helperId = this.helperText() ? this.selectId + '-helper' : null;
    const describedBy = this.ariaDescribedBy();
    return describedBy || helperId;
  });

  // Mapeamento interno para Material
  protected appearance = computed((): MatFormFieldAppearance => 'outline');

  protected formFieldClass = computed(() => {
    return [
      'os-select__form-field',
      `os-select__form-field--${this.size()}`,
      this.hasError() ? 'os-select__form-field--error' : '',
      this.disabled() ? 'os-select__form-field--disabled' : '',
    ]
      .filter(Boolean)
      .join(' ');
  });

  handleChange(event: { value: string | number }): void {
    const newValue = event.value;
    this.triggerHapticFeedback();
    this._onChange(newValue);
    this.valueChange.emit(newValue);
  }

  handleBlur(event: FocusEvent): void {
    this.isFocused.set(false);
    this._onTouched();
    this.blurEvent.emit(event);
  }

  handleFocus(event: FocusEvent): void {
    this.isFocused.set(true);
    this.focusEvent.emit(event);
  }

  onOpenedChange(isOpened: boolean): void {
    this.isOpened.set(isOpened);
    this.openedChange.emit(isOpened);
  }

  triggerHapticFeedback(): void {
    if (this.hapticFeedback() && 'vibrate' in navigator) {
      navigator.vibrate(50); // 50ms vibration
    }
  }

  writeValue(value: string | number): void {
    // Update the model signal when FormControl value changes programmatically
    if (value !== this.value()) {
      this.value.set(value);
    }
  }

  registerOnChange(fn: (value: string | number) => void): void {
    this._onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this._onTouched = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    // Update the disabled state when called by Angular Forms
    this.disabled.set(isDisabled);
  }
}

import { Component, computed, input, output, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { ChangeDetectionStrategy } from '@angular/core';
import { OsButtonComponent } from '../../atoms/os-button/os-button.component';
import { OsIconComponent } from '../../atoms/os-icon/os-icon.component';

export interface BreadcrumbItem {
  label: string;
  route?: string;
  disabled?: boolean;
}

export interface PageHeaderAction {
  label: string;
  icon?: string;
  variant?: 'primary' | 'secondary' | 'tertiary' | 'danger';
  size?: 'small' | 'medium' | 'large';
  disabled?: boolean;
  loading?: boolean;
}

@Component({
  selector: 'os-page-header',
  standalone: true,
  imports: [CommonModule, RouterModule, OsButtonComponent, OsIconComponent],
  template: `
    <header class="os-page-header" [class]="headerClasses()" [attr.aria-label]="ariaLabel()">
      <div class="os-page-header__content">
        @if (breadcrumbs().length > 0) {
        <nav class="os-page-header__breadcrumbs" aria-label="Breadcrumb">
          <ol class="os-page-header__breadcrumb-list">
            @for (item of breadcrumbs(); track $index; let isLast = $last) {
            <li class="os-page-header__breadcrumb-item">
              @if (item.route && !item.disabled && !isLast) {
              <a
                class="os-page-header__breadcrumb-link"
                [routerLink]="item.route"
                [attr.aria-current]="isLast ? 'page' : null"
              >
                {{ item.label }}
              </a>
              } @else {
              <span
                class="os-page-header__breadcrumb-text"
                [class]="getBreadcrumbClasses(item, isLast)"
                [attr.aria-current]="isLast ? 'page' : null"
              >
                {{ item.label }}
              </span>
              } @if (!isLast) {
              <os-icon
                name="chevron-right"
                size="sm"
                class="os-page-header__breadcrumb-separator"
                aria-hidden="true"
              />
              }
            </li>
            }
          </ol>
        </nav>
        }

        <div class="os-page-header__main">
          <div class="os-page-header__title-section">
            @if (icon()) {
            <os-icon
              [name]="icon()!"
              [size]="iconSize()"
              class="os-page-header__icon"
              [attr.aria-hidden]="true"
            />
            }
            <div class="os-page-header__title-content">
              <h1 class="os-page-header__title" [id]="titleId()">
                {{ title() }}
              </h1>
              @if (subtitle()) {
              <p class="os-page-header__subtitle" [id]="subtitleId()">
                {{ subtitle() }}
              </p>
              }
            </div>
          </div>

          @if (actions().length > 0) {
          <div class="os-page-header__actions">
            @for (action of actions(); track action.label) {
            <os-button
              [variant]="action.variant || 'secondary'"
              [size]="action.size || 'medium'"
              [disabled]="action.disabled || false"
              [loading]="action.loading || false"
              [icon]="action.icon || ''"
              (clicked)="onActionClick(action)"
            >
              {{ action.label }}
            </os-button>
            }
          </div>
          }
        </div>

        @if (description()) {
        <div class="os-page-header__description" [id]="descriptionId()">
          {{ description() }}
        </div>
        }
      </div>
    </header>
  `,
  styleUrls: ['./os-page-header.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class OsPageHeaderComponent {
  readonly title = input.required<string>();
  readonly subtitle = input<string | null>(null);
  readonly description = input<string | null>(null);
  readonly icon = input<string | null>(null);
  readonly breadcrumbs = input<BreadcrumbItem[]>([]);
  readonly actions = input<PageHeaderAction[]>([]);
  readonly variant = input<'default' | 'compact' | 'extended'>('default');
  readonly size = input<'small' | 'medium' | 'large'>('medium');
  readonly theme = input<'light' | 'dark'>('light');
  readonly ariaLabel = input<string | null>(null);

  readonly actionClick = output<PageHeaderAction>();

  readonly titleId = signal(`page-title-${Math.random().toString(36).substr(2, 9)}`);
  readonly subtitleId = signal(`page-subtitle-${Math.random().toString(36).substr(2, 9)}`);
  readonly descriptionId = signal(`page-description-${Math.random().toString(36).substr(2, 9)}`);

  readonly headerClasses = computed(() => {
    const classes = ['os-page-header'];
    classes.push(`os-page-header--${this.variant()}`);
    classes.push(`os-page-header--${this.size()}`);

    if (this.theme() === 'dark') {
      classes.push('os-page-header--dark');
    }

    if (this.breadcrumbs().length > 0) {
      classes.push('os-page-header--with-breadcrumbs');
    }

    if (this.actions().length > 0) {
      classes.push('os-page-header--with-actions');
    }

    if (this.description()) {
      classes.push('os-page-header--with-description');
    }

    return classes.join(' ');
  });

  readonly iconSize = computed(() => {
    const sizeMap: Record<string, 'sm' | 'md' | 'lg'> = {
      small: 'sm',
      medium: 'md',
      large: 'lg',
    };
    return sizeMap[this.size()];
  });

  getBreadcrumbClasses(item: BreadcrumbItem, isLast: boolean): string {
    const classes = ['os-page-header__breadcrumb-text'];

    if (item.disabled) {
      classes.push('os-page-header__breadcrumb-text--disabled');
    }

    if (isLast) {
      classes.push('os-page-header__breadcrumb-text--current');
    }

    return classes.join(' ');
  }

  onActionClick(action: PageHeaderAction): void {
    if (!action.disabled && !action.loading) {
      this.actionClick.emit(action);
    }
  }
}

