import { Injectable } from '@angular/core';
import {
  ExternalAuthServiceAdapter,
  AuthUser,
  AuthCredentials,
  AuthResult,
} from '../../app/core/adapters/external-auth-service.adapter';

@Injectable({
  providedIn: 'root',
})
export class MockAuthServiceAdapter implements ExternalAuthServiceAdapter {
  private readonly mockUser: AuthUser = {
    id: 'mock-user-id',
    email: 'dev@orca-sonhos.com',
    name: 'Developer User',
    avatar: null,
    metadata: {
      bypass: true,
      environment: 'development',
    },
  };

  private readonly mockToken = 'mock-bearer-token';
  private currentUser: AuthUser | null = null;

  initializeAuthState(callback: (user: AuthUser | null) => void): void {
    // Simular inicializao assncrona
    setTimeout(() => {
      this.currentUser = this.mockUser;
      callback(this.mockUser);
    }, 100);
  }

  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  async signIn(credentials: AuthCredentials): Promise<AuthResult> {
    // Simular delay de rede
    await new Promise((resolve) => setTimeout(resolve, 500));

    this.currentUser = this.mockUser;

    return {
      user: this.mockUser,
      token: this.mockToken,
      refreshToken: this.mockToken,
    };
  }

  async signOut(): Promise<void> {
    // Simular delay de rede
    await new Promise((resolve) => setTimeout(resolve, 300));

    this.currentUser = null;
  }

  async getToken(): Promise<string | null> {
    // Simular delay de rede
    await new Promise((resolve) => setTimeout(resolve, 50));

    return this.currentUser ? this.mockToken : null;
  }

  async refreshToken(): Promise<string | null> {
    // Simular delay de rede
    await new Promise((resolve) => setTimeout(resolve, 100));

    return this.currentUser ? this.mockToken : null;
  }

  getCurrentUser(): AuthUser | null {
    return this.currentUser;
  }

  isAuthenticated(): boolean {
    return this.currentUser !== null;
  }
}

import { Injectable, signal, computed } from '@angular/core';
import { OsAlertType } from '../../../shared/ui-components/molecules/os-alert/os-alert.component';

export interface Notification {
  id: string;
  type: OsAlertType;
  title?: string;
  message: string;
  dismissible?: boolean;
  duration?: number;
}

@Injectable({
  providedIn: 'root',
})
export class NotificationService {
  private readonly _notifications = signal<Notification[]>([]);
  private readonly _isLoading = signal<boolean>(false);

  readonly notifications = this._notifications.asReadonly();
  readonly isLoading = this._isLoading.asReadonly();
  readonly hasNotifications = computed(() => this._notifications().length > 0);

  showSuccess(message: string, title?: string, duration = 5000): string {
    return this.addNotification({
      type: 'success',
      message,
      title,
      dismissible: true,
      duration,
    });
  }

  showError(message: string, title?: string, duration = 8000): string {
    return this.addNotification({
      type: 'error',
      message,
      title,
      dismissible: true,
      duration,
    });
  }

  showWarning(message: string, title?: string, duration = 6000): string {
    return this.addNotification({
      type: 'warning',
      message,
      title,
      dismissible: true,
      duration,
    });
  }

  showInfo(message: string, title?: string, duration = 4000): string {
    return this.addNotification({
      type: 'info',
      message,
      title,
      dismissible: true,
      duration,
    });
  }

  setLoading(loading: boolean): void {
    this._isLoading.set(loading);
  }

  dismissNotification(id: string): void {
    this._notifications.update((notifications) =>
      notifications.filter((notification) => notification.id !== id)
    );
  }

  clearAll(): void {
    this._notifications.set([]);
  }

  private addNotification(notification: Omit<Notification, 'id'>): string {
    const id = `notification-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
    const newNotification: Notification = {
      ...notification,
      id,
    };

    this._notifications.update((notifications) => [...notifications, newNotification]);

    if (notification.duration && notification.duration > 0) {
      setTimeout(() => {
        this.dismissNotification(id);
      }, notification.duration);
    }

    return id;
  }
}

