computed(() => {
    if (this.text()) {
      return this.displayText();
    }
    if (this.dot()) {
      return '';
    }
    return null;
  })

computed(() => {
    switch (this.role()) {
      case 'status':
        return 'status';
      case 'alert':
        return 'alert';
      default:
        return null;
    }
  })

computed(() => {
    const text = this.text();
    if (!text) return '';

    // Verificar se  um nmero
    const numValue = parseInt(text, 10);
    if (!isNaN(numValue) && numValue > this.maxValue()) {
      return `${this.maxValue()}+`;
    }

    return text;
  })

computed(() => {
    const sizeMap: Record<OsBadgeSize, 'xs' | 'sm' | 'md' | 'lg'> = {
      sm: 'xs',
      md: 'sm',
      lg: 'md',
      xl: 'lg',
    };
    return sizeMap[this.size()];
  })

computed(() => {
    if (this.outlined()) {
      // Mapear variants especficos para variants de cone suportados
      switch (this.variant()) {
        case 'goal-active':
          return 'primary';
        case 'goal-completed':
          return 'success';
        case 'goal-overdue':
          return 'warning';
        default:
          return this.variant() as any;
      }
    }
    return 'default';
  })

computed(() => {
    switch (this.variant()) {
      case 'primary':
        return 'primary';
      case 'secondary':
        return 'accent';
      case 'success':
      case 'goal-completed':
        return 'primary';
      case 'warning':
      case 'goal-overdue':
        return 'warn';
      case 'error':
        return 'warn';
      case 'info':
      case 'goal-active':
        return 'accent';
      default:
        return undefined;
    }
  })

computed(() => {
    switch (this.position()) {
      case 'top-right':
        return 'above after';
      case 'top-left':
        return 'above before';
      case 'bottom-right':
        return 'below after';
      case 'bottom-left':
        return 'below before';
      default:
        return 'above after';
    }
  })

computed(() => {
    switch (this.size()) {
      case 'sm':
        return 'small';
      case 'md':
        return 'medium';
      case 'lg':
        return 'large';
      case 'xl':
        return 'large';
      default:
        return 'medium';
    }
  })

