.os-budget-tracker {
  &--loading {
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: 200px;
    padding: var(--os-spacing-lg);
  }

  &__loading {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: var(--os-spacing-md);
    color: var(--os-color-text-secondary);
  }

  &__loading-text {
    font-size: var(--os-font-size-sm);
    font-weight: var(--os-font-weight-medium);
  }

  &__header {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    margin-bottom: var(--os-spacing-lg);
    gap: var(--os-spacing-md);
  }

  &__title-section {
    flex: 1;
    min-width: 0;
  }

  &__title {
    font-size: var(--os-font-size-lg);
    font-weight: var(--os-font-weight-semibold);
    color: var(--os-color-text-primary);
    margin: 0 0 var(--os-spacing-xs) 0;
    line-height: 1.3;
  }

  &__category {
    font-size: var(--os-font-size-sm);
    color: var(--os-color-text-secondary);
    background: var(--os-color-background-secondary);
    padding: var(--os-spacing-xs) var(--os-spacing-sm);
    border-radius: var(--os-border-radius-sm);
    display: inline-block;
  }

  &__actions {
    display: flex;
    align-items: center;
    gap: var(--os-spacing-sm);
    flex-shrink: 0;
  }

  &__action-buttons {
    display: flex;
    gap: var(--os-spacing-xs);
  }

  &__action-btn {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 32px;
    height: 32px;
    border: 1px solid var(--os-color-border);
    background: var(--os-color-background);
    border-radius: var(--os-border-radius-sm);
    color: var(--os-color-text-secondary);
    cursor: pointer;
    transition: all var(--os-transition-fast);

    &:hover {
      background: var(--os-color-background-secondary);
      color: var(--os-color-text-primary);
      border-color: var(--os-color-primary);
    }

    &:focus {
      outline: 2px solid var(--os-color-primary);
      outline-offset: 2px;
    }
  }

  &__progress {
    margin-bottom: var(--os-spacing-lg);
  }

  &__progress-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: var(--os-spacing-sm);
  }

