    gap: var(--os-spacing-sm);
    padding: var(--os-spacing-sm);
    background: var(--os-color-background-secondary);
    border-radius: var(--os-border-radius-sm);
    font-size: var(--os-font-size-sm);
  }

  &__compact-percentage {
    font-weight: var(--os-font-weight-semibold);
    color: var(--os-color-text-primary);
  }

  &__compact-status {
    font-weight: var(--os-font-weight-medium);
  }

  &__compact-trend {
    font-size: var(--os-font-size-xs);
    font-weight: var(--os-font-weight-medium);
  }

  // Size variants
  &--small {
    .os-budget-tracker__title {
      font-size: var(--os-font-size-md);
    }

    .os-budget-tracker__amount-group {
      grid-template-columns: repeat(auto-fit, minmax(100px, 1fr));
      gap: var(--os-spacing-sm);
    }

    .os-budget-tracker__chart-bars {
      height: 80px;
    }
  }

  &--large {
    .os-budget-tracker__title {
      font-size: var(--os-font-size-xl);
    }

    .os-budget-tracker__amount-group {
      grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
      gap: var(--os-spacing-lg);
    }

    .os-budget-tracker__chart-bars {
      height: 160px;
    }
  }

  // Variant styles
  &--compact {
    .os-budget-tracker__header {
      margin-bottom: var(--os-spacing-md);
    }

    .os-budget-tracker__progress,
    .os-budget-tracker__financial,
    .os-budget-tracker__trends,
    .os-budget-tracker__projections,
    .os-budget-tracker__chart,
    .os-budget-tracker__dates {
      margin-bottom: var(--os-spacing-md);
    }
  }

  &--detailed {
    .os-budget-tracker__header {
      margin-bottom: var(--os-spacing-xl);
    }

    .os-budget-tracker__progress,
    .os-budget-tracker__financial,
    .os-budget-tracker__trends,
    .os-budget-tracker__projections,
    .os-budget-tracker__chart,
    .os-budget-tracker__dates {
      margin-bottom: var(--os-spacing-xl);
    }
  }

  // Responsive design
  @media (max-width: 768px) {
    &__header {
      flex-direction: column;
      align-items: stretch;
      gap: var(--os-spacing-sm);
    }

    &__actions {
      justify-content: space-between;
    }

    &__amount-group {
      grid-template-columns: 1fr;
      gap: var(--os-spacing-sm);
    }

    &__chart-bars {
      gap: var(--os-spacing-xs);
    }

    &__trend-info,
