    }
  }

  // Active effects
  &:active:not(&--disabled) {
    .os-checkbox__box {
      transform: scale(0.98);
    }
  }
}

// Keyframes para animaes
@keyframes os-checkbox-check {
  0% {
    transform: scale(0.8);
    opacity: 0.7;
  }
  50% {
    transform: scale(1.1);
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}

@keyframes os-checkbox-indeterminate {
  0% {
    transform: scale(0.8);
    opacity: 0.7;
  }
  50% {
    transform: scale(1.1);
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}

// Suporte para prefers-reduced-motion
@media (prefers-reduced-motion: reduce) {
  .os-checkbox {
    &--animated {
      transition: none;
    }

    .os-checkbox__box {
      transition: none;
    }
  }

  @keyframes os-checkbox-check {
    0%,
    100% {
      transform: none;
      opacity: 1;
    }
  }

  @keyframes os-checkbox-indeterminate {
    0%,
    100% {
      transform: none;
      opacity: 1;
    }
  }
}

