import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component, computed, input, output, signal } from '@angular/core';

import { OsButtonComponent } from '../../atoms/os-button/os-button.component';
import {
  OsDataTableAction,
  OsDataTableColumn,
  OsDataTableComponent,
} from '../../molecules/os-data-table/os-data-table.component';
import {
  OsFilterBarComponent,
  OsFilterOption,
} from '../../molecules/os-filter-bar/os-filter-bar.component';

export type OsDataGridSize = 'small' | 'medium' | 'large';
export type OsDataGridVariant = 'default' | 'compact' | 'detailed';

export interface OsDataGridFilter {
  key: string;
  value: string | number | Date | boolean;
  operator?: 'equals' | 'contains' | 'startsWith' | 'endsWith' | 'gt' | 'lt' | 'gte' | 'lte';
}

export interface OsDataGridSort {
  column: string;
  direction: 'asc' | 'desc';
}

export interface OsDataGridPagination {
  page: number;
  pageSize: number;
  total: number;
}

@Component({
  selector: 'os-data-grid',
  standalone: true,
  imports: [CommonModule, OsDataTableComponent, OsFilterBarComponent, OsButtonComponent],
  template: `
    <div class="os-data-grid" [class]="dataGridClasses()">
      <!-- Header -->
      @if (title() || showHeaderActions()) {
      <div class="os-data-grid__header">
        @if (title()) {
        <div class="os-data-grid__title-section">
          <h2 class="os-data-grid__title">{{ title() }}</h2>
          @if (subtitle()) {
          <p class="os-data-grid__subtitle">{{ subtitle() }}</p>
          }
        </div>
        } @if (showHeaderActions()) {
        <div class="os-data-grid__header-actions">
          @if (showRefreshButton()) {
          <os-button
            variant="tertiary"
            [size]="getButtonSize()"
            icon="refresh"
            [loading]="isLoading()"
            (buttonClick)="onRefresh()"
            [attr.aria-label]="'Atualizar dados'"
          >
            Atualizar
          </os-button>
          } @if (showExportButton()) {
          <os-button
            variant="secondary"
            [size]="getButtonSize()"
            icon="download"
            (buttonClick)="onExport()"
            [attr.aria-label]="'Exportar dados'"
          >
            Exportar
          </os-button>
          } @if (showAddButton()) {
          <os-button
            variant="primary"
            [size]="getButtonSize()"
            icon="add"
            (buttonClick)="onAdd()"
            [attr.aria-label]="'Adicionar novo item'"
          >
            {{ addButtonText() }}
          </os-button>
          }
        </div>
        }
      </div>
      }

      <!-- Filters -->
      @if (showFilters() && filterOptions().length > 0) {
      <div class="os-data-grid__filters">
        <os-filter-bar
          [variant]="getFilterBarVariant()"
          [size]="getFilterBarSize()"
          [hasActiveFilters]="hasActiveFilters()"
          (clear)="onClearFilters()"
          (apply)="onApplyFilters()"
        >
          <div class="os-data-grid__filter-content">
            @for (option of filterOptions(); track option.key) {
            <div class="os-data-grid__filter-item">
              <label [for]="'filter-' + option.key" class="os-data-grid__filter-label">{{
                option.label
              }}</label>
              @switch (option.type) { @case ('text') {
              <input
                [id]="'filter-' + option.key"
                type="text"
                class="os-data-grid__filter-input"
                [placeholder]="'Filtrar por ' + option.label"
                [value]="getFilterValue(option.key)"
                (input)="onFilterChange(option.key, $event)"
              />
              } @case ('select') {
              <select
                class="os-data-grid__filter-select"
                [value]="getFilterValue(option.key)"
                (change)="onFilterChange(option.key, $event)"
              >
                <option value="">{{ 'Todos os ' + option.label }}</option>
                @for (opt of option.options; track opt.value) {
                <option [value]="opt.value">{{ opt.label }}</option>
                }
              </select>
              } @case ('date') {
              <input
                type="date"
                class="os-data-grid__filter-input"
                [value]="getFilterValue(option.key)"
                (change)="onFilterChange(option.key, $event)"
              />
              } @case ('number') {
              <input
                type="number"
                class="os-data-grid__filter-input"
                [placeholder]="'Filtrar por ' + option.label"
                [value]="getFilterValue(option.key)"
                (input)="onFilterChange(option.key, $event)"
              />
              } }
            </div>
            }
          </div>
        </os-filter-bar>
      </div>
      }

      <!-- Data Table -->
      <div class="os-data-grid__table">
        <os-data-table
          [data]="filteredData()"
          [columns]="columns()"
          [actions]="tableActions()"
          [size]="getTableSize()"
          [variant]="getTableVariant()"
          [showPagination]="showPagination()"
          [showNoData]="showNoData()"
          [noDataText]="noDataText()"
          [pageSize]="pagination().pageSize"
          [pageIndex]="pagination().page"
          [totalItems]="totalItems()"
          [pageSizeOptions]="pageSizeOptions()"
          [showFirstLastButtons]="showFirstLastButtons()"
          (rowClick)="onRowClick($event)"
          (actionClick)="onTableActionClick($event)"
          (sortChange)="onSortChange($event)"
          (pageChange)="onPageChange($event)"
        />
      </div>

      <!-- Footer -->
      @if (showFooter()) {
      <div class="os-data-grid__footer">
        <div class="os-data-grid__footer-info">
          @if (showItemCount()) {
          <span class="os-data-grid__item-count">
            {{ getItemCountText() }}
          </span>
          } @if (showLastUpdated()) {
          <span class="os-data-grid__last-updated">
            ltima atualizao: {{ lastUpdated() | date : 'dd/MM/yyyy HH:mm' }}
          </span>
          }
        </div>

        @if (showFooterActions()) {
        <div class="os-data-grid__footer-actions">
          <ng-content select="[slot=footer-actions]"></ng-content>
        </div>
        }
      </div>
      }
    </div>
  `,
  styleUrl: './os-data-grid.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush,
  host: {
    class: 'os-data-grid-host',
  },
})
export class OsDataGridComponent {
  // Inputs
  data = input<Record<string, unknown>[]>([]);
  columns = input<OsDataTableColumn[]>([]);
  filterOptions = input<OsFilterOption[]>([]);
  tableActions = input<OsDataTableAction[]>([]);
  title = input<string>('');
  subtitle = input<string>('');
  size = input<OsDataGridSize>('medium');
  variant = input<OsDataGridVariant>('default');

  // Display options
  showHeaderActions = input<boolean>(true);
  showRefreshButton = input<boolean>(true);
  showExportButton = input<boolean>(true);
  showAddButton = input<boolean>(true);
  addButtonText = input<string>('Adicionar');
  showFilters = input<boolean>(true);
  showPagination = input<boolean>(true);
  showNoData = input<boolean>(true);
  noDataText = input<string>('Nenhum dado encontrado');
  showFooter = input<boolean>(true);
  showItemCount = input<boolean>(true);
  showLastUpdated = input<boolean>(true);
  showFooterActions = input<boolean>(false);

  // Pagination
  pageSizeOptions = input<number[]>([5, 10, 25, 50]);
  showFirstLastButtons = input<boolean>(true);

  // Loading state
  isLoading = input<boolean>(false);
  lastUpdated = input<Date>(new Date());

  // Outputs
  rowClick = output<Record<string, unknown>>();
  tableActionClick = output<OsDataTableAction>();
  refresh = output<void>();
  export = output<void>();
  add = output<void>();
  filterChange = output<OsDataGridFilter[]>();
  sortChange = output<OsDataGridSort>();
  pageChange = output<OsDataGridPagination>();

  // Internal state
  private filters = signal<OsDataGridFilter[]>([]);
  private sort = signal<OsDataGridSort | null>(null);
  protected pagination = signal<OsDataGridPagination>({
    page: 0,
    pageSize: 10,
    total: 0,
  });

  // Computed properties
  filteredData = computed(() => {
    let result = [...this.data()];

    // Apply filters
    const activeFilters = this.filters().filter(
      (f) => f.value !== null && f.value !== undefined && f.value !== ''
    );
    if (activeFilters.length > 0) {
      result = result.filter((item) => {
        return activeFilters.every((filter) => {
          const itemValue = this.getNestedValue(item, filter.key);
          return this.matchesFilter(itemValue, filter.value, filter.operator || 'contains');
        });
      });
    }

    // Apply sorting
    if (this.sort()) {
      result = this.applySorting(result, this.sort()!);
    }

    // Apply pagination
    const startIndex = this.pagination().page * this.pagination().pageSize;
    const endIndex = startIndex + this.pagination().pageSize;

    return result.slice(startIndex, endIndex);
  });

  // Computed for total items
  totalItems = computed(() => {
    let result = [...this.data()];

    // Apply filters
    const activeFilters = this.filters().filter(
      (f) => f.value !== null && f.value !== undefined && f.value !== ''
    );
    if (activeFilters.length > 0) {
      result = result.filter((item) => {
        return activeFilters.every((filter) => {
          const itemValue = this.getNestedValue(item, filter.key);
          return this.matchesFilter(itemValue, filter.value, filter.operator || 'contains');
        });
      });
    }

    return result.length;
  });

  hasActiveFilters = computed(() => {
    return this.filters().some((f) => f.value !== null && f.value !== undefined && f.value !== '');
  });

  // Methods
  dataGridClasses = () => {
    const classes = ['os-data-grid'];

    if (this.size() !== 'medium') {
      classes.push(`os-data-grid--${this.size()}`);
    }

    if (this.variant() !== 'default') {
      classes.push(`os-data-grid--${this.variant()}`);
    }

    if (this.isLoading()) {
      classes.push('os-data-grid--loading');
    }

    return classes.join(' ');
  };

  getButtonSize = () => {
    const sizeMap: Record<OsDataGridSize, 'small' | 'medium' | 'large'> = {
      small: 'small',
      medium: 'medium',
      large: 'large',
    };
    return sizeMap[this.size()];
  };

  getFilterBarVariant = () => {
    const variantMap: Record<OsDataGridVariant, 'default' | 'compact' | 'expanded'> = {
      default: 'default',
      compact: 'compact',
      detailed: 'expanded',
    };
    return variantMap[this.variant()];
  };

  getFilterBarSize = () => {
    const sizeMap: Record<OsDataGridSize, 'small' | 'medium' | 'large'> = {
      small: 'small',
      medium: 'medium',
      large: 'large',
    };
    return sizeMap[this.size()];
  };

  getTableSize = () => {
    const sizeMap: Record<OsDataGridSize, 'small' | 'medium' | 'large'> = {
      small: 'small',
      medium: 'medium',
      large: 'large',
    };
    return sizeMap[this.size()];
  };

  getTableVariant = () => {
    const variantMap: Record<OsDataGridVariant, 'default' | 'striped' | 'bordered'> = {
      default: 'default',
      compact: 'striped',
      detailed: 'bordered',
    };
    return variantMap[this.variant()];
  };

  getFilterValue = (key: string) => {
    const filter = this.filters().find((f) => f.key === key);
    return filter?.value || '';
  };

  getItemCountText = () => {
    const total = this.totalItems();
    const start = this.pagination().page * this.pagination().pageSize + 1;
    const end = Math.min(start + this.pagination().pageSize - 1, total);

    if (total === 0) {
      return 'Nenhum item encontrado';
    }

    return `Mostrando ${start}-${end} de ${total} itens`;
  };

  // Event handlers
  onRefresh(): void {
    this.refresh.emit();
  }

  onExport(): void {
    this.export.emit();
  }

  onAdd(): void {
    this.add.emit();
  }

  onRowClick(row: Record<string, unknown>): void {
    this.rowClick.emit(row);
  }

  onTableActionClick(action: OsDataTableAction): void {
    this.tableActionClick.emit(action);
  }

  onFilterChange(key: string, event: Event | { target?: { value: unknown } } | unknown): void {
    const value = (event as { target?: { value: unknown } })?.target?.value || event;
    this.updateFilter(key, value as string | number | Date | boolean | null | undefined);
  }

  onClearFilters(): void {
    this.filters.set([]);
    this.filterChange.emit([]);
  }

  onApplyFilters(): void {
    this.filterChange.emit([...this.filters()]);
  }

  onSortChange(sort: { active: string; direction: 'asc' | 'desc' | '' }): void {
    if (sort.direction === '') return;

    this.sort.set({
      column: sort.active,
      direction: sort.direction as 'asc' | 'desc',
    });
    this.sortChange.emit(this.sort()!);
  }

  onPageChange(event: { pageIndex: number; pageSize: number }): void {
    this.pagination.update((p) => ({
      ...p,
      page: event.pageIndex,
      pageSize: event.pageSize,
    }));
    this.pageChange.emit(this.pagination());
  }

  // Helper methods
  private updateFilter(
    key: string,
    value: string | number | Date | boolean | null | undefined
  ): void {
    const currentFilters = [...this.filters()];
    const existingIndex = currentFilters.findIndex((f) => f.key === key);

    if (value === null || value === undefined || value === '') {
      if (existingIndex >= 0) {
        currentFilters.splice(existingIndex, 1);
      }
    } else {
      const filter: OsDataGridFilter = { key, value };
      if (existingIndex >= 0) {
        currentFilters[existingIndex] = filter;
      } else {
        currentFilters.push(filter);
      }
    }

    this.filters.set(currentFilters);
  }

  private getNestedValue(obj: Record<string, unknown>, path: string): unknown {
    return path
      .split('.')
      .reduce((current: unknown, key: string) => (current as Record<string, unknown>)?.[key], obj);
  }

  private matchesFilter(itemValue: unknown, filterValue: unknown, operator: string): boolean {
    if (itemValue === null || itemValue === undefined) {
      return false;
    }

    const itemStr = String(itemValue).toLowerCase();
    const filterStr = String(filterValue).toLowerCase();

    switch (operator) {
      case 'equals':
        return itemStr === filterStr;
      case 'contains':
        return itemStr.includes(filterStr);
      case 'startsWith':
        return itemStr.startsWith(filterStr);
      case 'endsWith':
        return itemStr.endsWith(filterStr);
      case 'gt':
        return Number(itemValue) > Number(filterValue);
      case 'lt':
        return Number(itemValue) < Number(filterValue);
      case 'gte':
        return Number(itemValue) >= Number(filterValue);
      case 'lte':
        return Number(itemValue) <= Number(filterValue);
      default:
        return itemStr.includes(filterStr);
    }
  }

  private applySorting(
    data: Record<string, unknown>[],
    sort: OsDataGridSort
  ): Record<string, unknown>[] {
    return [...data].sort((a, b) => {
      const aValue = this.getNestedValue(a, sort.column);
      const bValue = this.getNestedValue(b, sort.column);

      if (aValue === bValue) return 0;

      const aStr = String(aValue || '');
      const bStr = String(bValue || '');
      const comparison = aStr < bStr ? -1 : aStr > bStr ? 1 : 0;
      return sort.direction === 'asc' ? comparison : -comparison;
    });
  }
}

import { Component, input, output, ChangeDetectionStrategy, computed } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatTableModule } from '@angular/material/table';
import { MatSortModule, Sort } from '@angular/material/sort';
import { MatPaginatorModule, PageEvent } from '@angular/material/paginator';
import { OsButtonComponent } from '../../atoms/os-button/os-button.component';
import { OsIconComponent } from '../../atoms/os-icon/os-icon.component';

export type OsDataTableSize = 'small' | 'medium' | 'large';
export type OsDataTableVariant = 'default' | 'striped' | 'bordered';

export interface OsDataTableColumn {
  key: string;
  label: string;
  sortable?: boolean;
  width?: string;
  align?: 'left' | 'center' | 'right';
}

export type OsDataTableRow = Record<string, unknown>;

export interface OsDataTableAction {
  key: string;
  label: string;
  icon?: string;
  color?: 'primary' | 'secondary' | 'warn';
}

@Component({
  selector: 'os-data-table',
  standalone: true,
  imports: [
    CommonModule,
    MatTableModule,
    MatSortModule,
    MatPaginatorModule,
    OsButtonComponent,
    OsIconComponent,
  ],
  template: `
    <div class="os-data-table" [class]="dataTableClasses()">
      @if (title()) {
      <div class="os-data-table__header">
        <h3 class="os-data-table__title">{{ title() }}</h3>
        @if (showActions() && actions().length > 0) {
        <div class="os-data-table__actions">
          @for (action of actions(); track action.key) {
          <os-button
            [variant]="getButtonVariant(action.color)"
            [size]="getButtonSize()"
            [icon]="action.icon || ''"
            (buttonClick)="onActionClick(action)"
            [attr.aria-label]="action.label"
          >
            {{ action.label }}
          </os-button>
          }
        </div>
        }
      </div>
      }

      <div class="os-data-table__content">
        <table mat-table [dataSource]="data()" matSort class="os-data-table__table">
          @for (column of columns(); track column.key) {
          <ng-container [matColumnDef]="column.key">
            <th
              mat-header-cell
              *matHeaderCellDef
              [class]="headerCellClass(column)"
              [style.width]="column.width || 'auto'"
              [style.text-align]="column.align || 'left'"
              [mat-sort-header]="column.sortable ? column.key : ''"
            >
              {{ column.label }}
            </th>
            <td
              mat-cell
              *matCellDef="let row"
              [class]="cellClass(column)"
              [style.text-align]="column.align || 'left'"
            >
              {{ getCellValue(row, column.key) }}
            </td>
          </ng-container>
          }

          <tr mat-header-row *matHeaderRowDef="displayedColumns()"></tr>
          <tr
            mat-row
            *matRowDef="let row; columns: displayedColumns()"
            [class]="rowClass(row)"
            (click)="onRowClick(row)"
          ></tr>
        </table>

        @if (showNoData() && data().length === 0) {
        <div class="os-data-table__no-data">
          <os-icon name="inbox" size="lg" />
          <p>{{ noDataText() }}</p>
        </div>
        }
      </div>

      @if (showPagination() && data().length > 0) {
      <mat-paginator
        [length]="totalItems()"
        [pageSize]="pageSize()"
        [pageIndex]="pageIndex()"
        [pageSizeOptions]="pageSizeOptions()"
        [showFirstLastButtons]="showFirstLastButtons()"
        (page)="onPageChange($event)"
        class="os-data-table__paginator"
      ></mat-paginator>
      }
    </div>
  `,
  styleUrl: './os-data-table.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush,
  host: {
    class: 'os-data-table-host',
  },
})
export class OsDataTableComponent {
  data = input<OsDataTableRow[]>([]);
  columns = input<OsDataTableColumn[]>([]);
  title = input<string>('');
  actions = input<OsDataTableAction[]>([]);
  size = input<OsDataTableSize>('medium');
  variant = input<OsDataTableVariant>('default');
  sortable = input<boolean>(true);
  sortActive = input<string>('');
  sortDirection = input<'asc' | 'desc'>('asc');
  showActions = input<boolean>(true);
  showPagination = input<boolean>(true);
  showNoData = input<boolean>(true);
  noDataText = input<string>('Nenhum dado encontrado');
  pageSize = input<number>(10);
  pageIndex = input<number>(0);
  totalItems = input<number>(0);
  pageSizeOptions = input<number[]>([5, 10, 25, 50]);
  showFirstLastButtons = input<boolean>(true);

  rowClick = output<OsDataTableRow>();
  actionClick = output<OsDataTableAction>();
  sortChange = output<Sort>();
  pageChange = output<PageEvent>();

  protected sort = true;

  displayedColumns = computed(() => this.columns().map((col) => col.key));

  // Mapeamento interno para Atoms
  protected getButtonVariant = (color?: string) => {
    const colorMap: Record<string, 'primary' | 'secondary' | 'tertiary' | 'danger'> = {
      primary: 'primary',
      secondary: 'secondary',
      warn: 'danger',
    };
    return colorMap[color || 'primary'] || 'primary';
  };

  protected getButtonSize = () => {
    const sizeMap: Record<OsDataTableSize, 'small' | 'medium' | 'large'> = {
      small: 'small',
      medium: 'medium',
      large: 'large',
    };
    return sizeMap[this.size()];
  };

  dataTableClasses = () => {
    const classes = ['os-data-table'];

    if (this.size() !== 'medium') {
      classes.push(`os-data-table--${this.size()}`);
    }

    if (this.variant() !== 'default') {
      classes.push(`os-data-table--${this.variant()}`);
    }

    return classes.join(' ');
  };

  headerCellClass = (column: OsDataTableColumn) => {
    const classes = ['os-data-table__header-cell'];

    if (column.sortable) {
      classes.push('os-data-table__header-cell--sortable');
    }

    return classes.join(' ');
  };

  cellClass = (column: OsDataTableColumn) => {
    const classes = ['os-data-table__cell'];

    if (column.align) {
      classes.push(`os-data-table__cell--${column.align}`);
    }

    return classes.join(' ');
  };

  rowClass = (row: OsDataTableRow) => {
    const classes = ['os-data-table__row'];

    if (this.variant() === 'striped') {
      const index = this.data().indexOf(row);
      if (index % 2 === 1) {
        classes.push('os-data-table__row--striped');
      }
    }

    return classes.join(' ');
  };

  getCellValue = (row: OsDataTableRow, key: string) => {
    return row[key] || '';
  };

  onRowClick(row: OsDataTableRow): void {
    this.rowClick.emit(row);
  }

  onActionClick(action: OsDataTableAction): void {
    this.actionClick.emit(action);
  }

  onSortChange(sort: Sort): void {
    this.sortChange.emit(sort);
  }

  onPageChange(event: PageEvent): void {
    this.pageChange.emit(event);
  }
}

propertiesClass.defaultValue:
computed(() => {
    const helperId = this.helperText() ? this.inputId + '-helper' : null;
    const describedBy = this.ariaDescribedBy();
    return describedBy || helperId;
  })

computed(() => {
    return [
      'os-date-input-container',
      `os-date-input-container--${this.size()}`,
      this.hasError() ? 'os-date-input-container--error' : '',
      this.disabled() ? 'os-date-input-container--disabled' : '',
      this.isFocused() ? 'os-date-input-container--focused' : '',
      this.isHovered() ? 'os-date-input-container--hovered' : '',
      this.animated() ? 'os-date-input-container--animated' : '',
    ]
      .filter(Boolean)
      .join(' ');
  })

computed(() => {
    const value = this.value();
    if (!value) return '';
    return this.formatDateForInput(value);
  })

