.os-form-field {
  display: flex;
  flex-direction: column;
  gap: var(--os-spacing-xs);
  width: 100%;
  position: relative;

  &--small {
    gap: var(--os-spacing-xs);
  }

  &--medium {
    gap: var(--os-spacing-sm);
  }

  &--large {
    gap: var(--os-spacing-md);
  }

  &--default {
  }

  &--outlined {
    .os-form-field__hint {
      border-left: var(--os-border-width-thin) solid var(--os-color-primary-300);
      padding-left: var(--os-spacing-sm);
    }
  }

  &--filled {
    background-color: var(--os-color-surface-variant);
    padding: var(--os-spacing-sm);
    border-radius: var(--os-border-radius-md);
    border: var(--os-border-width-thin) solid transparent;

    &:focus-within {
      border-color: var(--os-color-primary-500);
      background-color: var(--os-color-surface);
    }
  }

  &--disabled {
    opacity: 0.6;
    pointer-events: none;
  }

  &--error {
    .os-form-field__hint {
      color: var(--os-color-error);
    }
  }

  &--required {
  }

  &--touched {
    .os-form-field__validation-icon {
      opacity: 1;
    }
  }

  &--dirty {
    .os-form-field__validation-icon {
      opacity: 1;
    }
  }

  &__input-container {
    position: relative;
    display: flex;
    align-items: center;
  }

  &__validation-icon {
    position: absolute;
    right: var(--os-spacing-sm);
    top: 50%;
    transform: translateY(-50%);
    opacity: 0;
    transition: opacity var(--os-transition-fast);
    z-index: 1;
    pointer-events: none;
  }

  &__error-message {
    display: flex;
    align-items: center;
    gap: var(--os-spacing-xs);
    font-size: var(--os-font-size-sm);
    color: var(--os-color-error);
    margin-top: var(--os-spacing-xs);
    font-weight: var(--os-font-weight-medium);
    line-height: var(--os-line-height-tight);
    animation: shake 0.3s ease-in-out;

    @media (prefers-reduced-motion: reduce) {
      animation: none;
    }
  }

  &__hint {
    font-size: var(--os-font-size-sm);
    color: var(--os-color-on-surface-variant);
    margin-top: var(--os-spacing-xs);
    transition: color var(--os-transition-fast);
    line-height: var(--os-line-height-normal);

    &--small {
      font-size: var(--os-font-size-xs);
    }

    &--medium {
      font-size: var(--os-font-size-sm);
    }

    &--large {
      font-size: var(--os-font-size-base);
    }

    &--disabled {
      color: var(--os-color-on-surface-variant);
      opacity: 0.6;
    }
  }

  &__character-count {
    font-size: var(--os-font-size-xs);
    color: var(--os-color-on-surface-variant);
    margin-top: var(--os-spacing-xs);
    text-align: right;
    opacity: 0.7;
    transition: color var(--os-transition-fast);

    &:focus-within {
      color: var(--os-color-primary-600);
    }
  }

  @media (max-width: 768px) {
    &--large {
      gap: var(--os-spacing-sm);
    }

    &__validation-icon {
      right: var(--os-spacing-xs);
    }
  }

  @media (max-width: 480px) {
    gap: var(--os-spacing-xs);

    &__hint {
      font-size: var(--os-font-size-xs);
    }

    &__error-message {
      font-size: var(--os-font-size-xs);
    }

    &__character-count {
      font-size: var(--os-font-size-xs);
    }
  }
}

.os-form-field:focus-within {
  .os-form-field__hint {
    color: var(--os-color-primary-600);
  }
}

.os-form-field--error {
  .os-form-field__hint {
    color: var(--os-color-error);
    font-weight: var(--os-font-weight-medium);
  }
}

.os-form-field {
  &:focus-within {
    outline: none;
  }

  @media (prefers-contrast: high) {
    &--error {
      .os-form-field__hint {
        color: var(--os-color-error);
        font-weight: var(--os-font-weight-bold);
      }

      .os-form-field__error-message {
        color: var(--os-color-error);
        font-weight: var(--os-font-weight-bold);
      }
    }
  }

  @media (prefers-reduced-motion: reduce) {
    .os-form-field__hint,
    .os-form-field__validation-icon,
    .os-form-field__character-count {
      transition: none;
    }

    .os-form-field__error-message {
      animation: none;
    }
  }
}

@keyframes shake {
  0%,
  100% {
    transform: translateX(0);
  }
  10%,
  30%,
  50%,
  70%,
  90% {
    transform: translateX(-2px);
  }
  20%,
  40%,
  60%,
  80% {
    transform: translateX(2px);
  }
}

[data-theme='dark'] {
  .os-form-field {
    &--filled {
      background-color: var(--os-color-surface-variant);

      &:focus-within {
        background-color: var(--os-color-surface);
        border-color: var(--os-color-primary-400);
      }
    }

    &__hint {
      color: var(--os-color-on-surface-variant);
    }

    &__character-count {
      color: var(--os-color-on-surface-variant);
    }
  }
}

// High contrast mode support
@media (prefers-contrast: high) {
  .os-form-field {
    &--error {
      .os-form-field__error-message {
        border: 1px solid var(--os-color-error);
        padding: var(--os-spacing-xs);
        border-radius: var(--os-border-radius-sm);
      }
    }
  }
}

// Touch target optimization for mobile
@media (max-width: 768px) {
  .os-form-field {
    &__validation-icon {
      width: 20px;
      height: 20px;
    }
  }
}

