@use '../../theme/variables' as *;
@use '../../theme/tokens' as *;

.os-goal-progress {
  display: flex;
  flex-direction: column;
  gap: $spacing-md;
  padding: $spacing-lg;
  border-radius: $border-radius-lg;
  background: $color-background-primary;
  border: 1px solid $color-border;
  transition: all 0.2s ease-in-out;

  &--small {
    padding: $spacing-md;
    gap: $spacing-sm;
  }

  &--large {
    padding: $spacing-xl;
    gap: $spacing-lg;
  }

  &--compact {
    padding: $spacing-md;
    gap: $spacing-sm;
  }

  &--detailed {
    padding: $spacing-xl;
    gap: $spacing-lg;
  }

  &--minimal {
    padding: $spacing-sm;
    gap: $spacing-xs;
    border: none;
    background: transparent;
  }

  &--dark {
    background: $color-surface-variant;
    border-color: $color-border;
    color: $color-on-surface;
  }

  &--completed {
    border-color: $color-success;
    background: rgba($color-success, 0.05);
  }

  &--overdue {
    border-color: $color-error;
    background: rgba($color-error, 0.05);
  }

  &--priority-high {
    border-left: 4px solid $color-warning;
  }

  &--priority-medium {
    border-left: 4px solid $color-info;
  }

  &--priority-low {
    border-left: 4px solid $color-border;
  }

  &__header {
    display: flex;
    align-items: flex-start;
    gap: $spacing-md;
  }

  &__icon {
    flex-shrink: 0;
    margin-top: $spacing-xs;
  }

  &__title-section {
    flex: 1;
    min-width: 0;
  }

  &__title {
    margin: 0 0 $spacing-xs 0;
    font-size: $font-size-lg;
    font-weight: $font-weight-semibold;
    color: $color-text-primary;
    line-height: $line-height-tight;
  }

  &__description {
    margin: 0;
    font-size: $font-size-sm;
    color: $color-text-secondary;
    line-height: $line-height-relaxed;
  }

  &__category {
    flex-shrink: 0;
  }

  &__category-text {
    display: inline-block;
    padding: $spacing-xs $spacing-sm;
    font-size: $font-size-xs;
    font-weight: $font-weight-medium;
    color: $color-text-secondary;
    background: $color-background-secondary;
    border-radius: $border-radius-sm;
    text-transform: uppercase;
    letter-spacing: 0.5px;
  }

  &__content {
    display: flex;
    flex-direction: column;
    gap: $spacing-md;
  }

  &__amounts {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: $spacing-md;
  }

  &__current,
  &__target {
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
  }

  &__current-label,
  &__target-label {
    font-size: $font-size-xs;
    color: $color-text-secondary;
    margin-top: $spacing-xs;
    font-weight: $font-weight-medium;
  }

  &__progress {
    width: 100%;
    position: relative;
  }

  &__milestone {
    display: flex;
    align-items: center;
    gap: $spacing-xs;
    margin-top: $spacing-sm;
    padding: $spacing-xs $spacing-sm;
    background: rgba($color-success, 0.1);
    border: 1px solid rgba($color-success, 0.3);
    border-radius: $border-radius-sm;
    animation: milestone-entrance 0.5s ease-out;
  }

  &__milestone-text {
    font-size: $font-size-xs;
    font-weight: $font-weight-medium;
    color: $color-success;
  }

  &__celebration {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    background: rgba($color-success, 0.05);
    border-radius: $border-radius-lg;
    z-index: 10;
    animation: celebration-entrance 0.6s ease-out;
  }

  &__celebration-content {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: $spacing-sm;
    text-align: center;
  }

  &__celebration-text {
    font-size: $font-size-lg;
    font-weight: $font-weight-bold;
    color: $color-success;
    animation: celebration-pulse 1s ease-in-out infinite;
  }

  &__confetti {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    pointer-events: none;
    overflow: hidden;
  }

  &__confetti-piece {
    position: absolute;
    width: 8px;
    height: 8px;
    background: $color-success;
    border-radius: 50%;
    animation: confetti-fall 3s ease-out infinite;

    &:nth-child(1) {
      left: 10%;
      animation-delay: 0s;
      background: $color-success;
    }

    &:nth-child(2) {
      left: 20%;
      animation-delay: 0.1s;
      background: $color-warning;
    }

    &:nth-child(3) {
      left: 30%;
      animation-delay: 0.2s;
      background: $color-error;
    }

    &:nth-child(4) {
      left: 40%;
      animation-delay: 0.3s;
      background: $color-info;
    }

    &:nth-child(5) {
      left: 50%;
      animation-delay: 0.4s;
      background: $color-success;
    }

    &:nth-child(6) {
      left: 60%;
      animation-delay: 0.5s;
      background: $color-warning;
    }

    &:nth-child(7) {
      left: 70%;
      animation-delay: 0.6s;
      background: $color-error;
    }

    &:nth-child(8) {
      left: 80%;
      animation-delay: 0.7s;
      background: $color-info;
    }
  }

  &__stats {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: $spacing-md;
    padding: $spacing-sm;
    background: $color-background-secondary;
    border-radius: $border-radius-md;
  }

  &__stat {
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
  }

  &__stat-label {
    font-size: $font-size-xs;
    color: $color-text-secondary;
    margin-bottom: $spacing-xs;
    font-weight: $font-weight-medium;
  }

  &__stat-value {
    font-size: $font-size-sm;
    font-weight: $font-weight-semibold;
    color: $color-text-primary;
  }

  &__actions {
    display: flex;
    gap: $spacing-sm;
    justify-content: flex-end;
    padding-top: $spacing-md;
    border-top: 1px solid $color-border;
  }

  @media (max-width: 768px) {
    &__header {
      flex-direction: column;
      align-items: flex-start;
      gap: $spacing-sm;
    }

    &__amounts {
      flex-direction: column;
      align-items: stretch;
      gap: $spacing-sm;
    }

    &__current,
    &__target {
      flex-direction: row;
      justify-content: space-between;
      align-items: center;
    }

    &__stats {
      flex-direction: column;
      align-items: stretch;
      gap: $spacing-sm;
    }

    &__stat {
      flex-direction: row;
      justify-content: space-between;
      align-items: center;
    }

    &__actions {
      flex-direction: column;
      align-items: stretch;
    }
  }

  &--dark {
    .os-goal-progress__title {
      color: $color-on-surface;
    }

    .os-goal-progress__description {
      color: $color-on-surface-variant;
    }

    .os-goal-progress__category-text {
      background: $color-surface;
      color: $color-on-surface-variant;
    }

    .os-goal-progress__stat-label {
      color: $color-on-surface-variant;
    }

    .os-goal-progress__stat-value {
      color: $color-on-surface;
    }

    .os-goal-progress__actions {
      border-top-color: $color-border;
    }
  }

  &:hover {
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
    transform: translateY(-1px);
  }

  &--minimal:hover {
    box-shadow: none;
    transform: none;
  }

  &:focus-within {
    outline: 2px solid $color-primary;
    outline-offset: 2px;
  }
}

@keyframes milestone-entrance {
  0% {
    opacity: 0;
    transform: translateY(-10px) scale(0.9);
  }
  100% {
    opacity: 1;
    transform: translateY(0) scale(1);
  }
}

@keyframes celebration-entrance {
  0% {
    opacity: 0;
    transform: scale(0.8);
  }
  50% {
    opacity: 1;
    transform: scale(1.1);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes celebration-pulse {
  0%,
  100% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.05);
  }
}

@keyframes confetti-fall {
  0% {
    transform: translateY(-100vh) rotate(0deg);
    opacity: 1;
  }
  100% {
    transform: translateY(100vh) rotate(360deg);
    opacity: 0;
  }
}

@media (prefers-reduced-motion: reduce) {
  .os-goal-progress__milestone,
  .os-goal-progress__celebration,
  .os-goal-progress__celebration-text,
  .os-goal-progress__confetti-piece {
    animation: none;
  }
}

