<div
  [class]="iconClass()"
  [attr.aria-hidden]="ariaHidden()"
  [attr.aria-label]="ariaLabel() || null"
  [attr.title]="title() || null"
  [innerHTML]="svgContent()"
  role="img"
></div>
} @else if (svgUrl()) {
<img
  [class]="iconClass()"
  [attr.aria-hidden]="ariaHidden()"
  [attr.aria-label]="ariaLabel() || null"
  [attr.title]="title() || null"
  [src]="svgUrl()"
  [alt]="ariaLabel() || ''"
  role="img"
/>
} @else {
<mat-icon
  [class]="iconClass()"
  [attr.aria-hidden]="ariaHidden()"
  [attr.aria-label]="ariaLabel() || null"
  [attr.title]="title() || null"
  [fontSet]="fontSet()"
  [fontIcon]="fontIcon()"
  [color]="matColor()"
  [inline]="inline()"
  [attr.role]="iconRole()"
>
  @if (!fontIcon()) {
  {{ iconContent() }}
  }
</mat-icon>
}

