computed(() => {
    const iconName = this.name();
    if (!iconName) return this.fallbackIcon();

    // Verificar se o cone  suportado
    if (!this.supportedIcons.has(iconName)) {
      console.warn(`cone "${iconName}" no  suportado. Usando fallback "${this.fallbackIcon()}"`);
      return this.getIconContent(this.fallbackIcon());
    }

    return this.getIconContent(iconName);
  })

computed(() => {
    switch (this.role()) {
      case 'informative':
        return 'img';
      case 'interactive':
        return 'button';
      default:
        return null;
    }
  })

computed(() => {
    switch (this.variant()) {
      case 'primary':
        return 'primary';
      case 'secondary':
        return 'accent';
      case 'success':
        return 'primary';
      case 'warning':
        return 'warn';
      case 'error':
        return 'warn';
      case 'info':
        return 'accent';
      default:
        return undefined;
    }
  })

styleUrlsData.data:
.os-icon {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  font-style: normal;
  font-variant: normal;
  text-rendering: auto;
  line-height: 1;
  user-select: none;

  // Override Material icon styles
  ::ng-deep mat-icon {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    font-style: normal;
    font-variant: normal;
    text-rendering: auto;
    line-height: 1;
    user-select: none;
  }

  &--xs {
    font-size: var(--os-font-size-xs);
    width: 12px;
    height: 12px;
  }

  &--sm {
    font-size: var(--os-font-size-sm);
    width: 16px;
    height: 16px;
  }

  &--md {
    font-size: var(--os-font-size-base);
    width: 20px;
    height: 20px;
  }

  &--lg {
    font-size: var(--os-font-size-lg);
    width: 24px;
    height: 24px;
  }

  &--xl {
    font-size: var(--os-font-size-xl);
    width: 28px;
    height: 28px;
  }

  &--2xl {
    font-size: var(--os-font-size-2xl);
    width: 32px;
    height: 32px;
  }

  &--default {
    color: var(--os-color-neutral-600);
  }

  &--primary {
    color: var(--os-color-primary-600);
  }

  &--secondary {
    color: var(--os-color-secondary-600);
  }

  &--success {
    color: var(--os-color-success);
  }

  &--warning {
    color: var(--os-color-warning);
  }

  &--error {
    color: var(--os-color-error);
  }

  &--info {
    color: var(--os-color-info);
  }

  // Role-based styles
  &--decorative {
    // cones decorativos no precisam de foco
  }

  &--informative {
    // cones informativos podem ter foco para screen readers
    &:focus {
      outline: 2px solid var(--os-color-primary-500);
      outline-offset: 2px;
      border-radius: var(--os-border-radius-sm);
    }
  }

  &--interactive {
    // cones interativos precisam de foco e cursor
    cursor: pointer;

    &:focus {
      outline: 2px solid var(--os-color-primary-500);
      outline-offset: 2px;
      border-radius: var(--os-border-radius-sm);
    }

    &:hover {
      opacity: 0.8;
      transform: scale(1.05);
      transition: all var(--os-transition-fast) ease-in-out;
    }

    &:active {
      transform: scale(0.95);
    }
  }

  &--spin {
    animation: spin 1s linear infinite;
  }

  &--pulse {
    animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  // SVG support
  svg {
    width: 100%;
    height: 100%;
    fill: currentColor;
  }

  img {
    width: 100%;
    height: 100%;
    object-fit: contain;
  }

  &:hover {
    opacity: 0.8;
    transition: opacity var(--os-transition-fast) ease-in-out;
  }

  &:focus {
    outline: 2px solid var(--os-color-primary-500);
    outline-offset: 2px;
    border-radius: var(--os-border-radius-sm);
  }
}

@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@keyframes pulse {
  0%,
  100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}

@media (max-width: 768px) {
  .os-icon {
    &--xs {
      width: 14px;
      height: 14px;
    }

    &--sm {
      width: 18px;
      height: 18px;
    }

    &--md {
      width: 22px;
      height: 22px;
    }

    &--lg {
      width: 26px;
      height: 26px;
    }

    &--xl {
      width: 30px;
      height: 30px;
    }

    &--2xl {
      width: 34px;
      height: 34px;
    }
  }
}

[data-theme='dark'] {
  .os-icon {
    &--default {
      color: var(--os-color-neutral-400);
    }

    &--primary {
      color: var(--os-color-primary-400);
    }

    &--secondary {
      color: var(--os-color-secondary-400);
    }

    &:hover {
      opacity: 0.9;
    }
  }
}

@media (prefers-contrast: high) {
  .os-icon {
    &--default {
      color: var(--os-color-neutral-800);
    }

    &--primary {
      color: var(--os-color-primary-800);
    }

    &--secondary {
      color: var(--os-color-secondary-800);
    }
  }
}

@media (prefers-reduced-motion: reduce) {
  .os-icon {
    &--spin,
    &--pulse {
      animation: none;
    }
  }
}

// Contraste automtico para diferentes backgrounds
.os-icon {
  // Background claro
  &[data-background='light'] {
    &.os-icon--default {
      color: var(--os-color-neutral-700);
    }
    &.os-icon--primary {
      color: var(--os-color-primary-700);
    }
    &.os-icon--secondary {
      color: var(--os-color-secondary-700);
    }
  }

  // Background escuro
  &[data-background='dark'] {
    &.os-icon--default {
      color: var(--os-color-neutral-300);
    }
    &.os-icon--primary {
      color: var(--os-color-primary-300);
    }
    &.os-icon--secondary {
      color: var(--os-color-secondary-300);
    }
  }

  // Background colorido
  &[data-background='colored'] {
    &.os-icon--default {
      color: var(--os-color-neutral-800);
    }
    &.os-icon--primary {
      color: var(--os-color-primary-800);
    }
    &.os-icon--secondary {
      color: var(--os-color-secondary-800);
    }
  }
}

// Fallback para cones no encontrados
.os-icon--fallback {
  &::before {
    content: '?';
    font-weight: bold;
    color: var(--os-color-warning);
  }
}

