// OS-INPUT - Layout Specification Implementation
// Seguindo especificao detalhada para alinhamento com viso de produto

.os-input-container {
  display: flex;
  flex-direction: column;
  gap: var(--os-spacing-xs);
  width: 100%;

  &--small {
    gap: calc(var(--os-spacing-xs) * 0.5);
  }

  &--large {
    gap: var(--os-spacing-sm);
  }

  &--error {
    .os-input {
      border-color: var(--os-color-error-500);
      box-shadow: 0 0 0 1px var(--os-color-error-500);
    }
  }

  &--disabled {
    opacity: 0.6;
    pointer-events: none;
  }
}

.os-input__form-field {
  width: 100%;

  &--small {
    .mat-mdc-form-field {
      font-size: var(--os-font-size-sm);
    }
  }

  &--medium {
    .mat-mdc-form-field {
      font-size: var(--os-font-size-base);
    }
  }

  &--large {
    .mat-mdc-form-field {
      font-size: var(--os-font-size-lg);
    }
  }

  &--error {
    .mat-mdc-form-field {
      .mat-mdc-form-field-focus-overlay {
        background-color: var(--os-color-error-50);
      }
    }
  }

  &--disabled {
    .mat-mdc-form-field {
      opacity: 0.6;
    }
  }
}

.os-input {
  width: 100%;
  border: 1px solid var(--os-color-neutral-300);
  border-radius: 6px;
  background-color: var(--os-color-background-primary);
  color: var(--os-color-text-primary);
  font-family: var(--os-font-family-primary);
  font-size: var(--os-font-size-sm);
  font-weight: var(--os-font-weight-regular);
  transition: all 200ms ease-in-out;
  outline: none;

  &--small {
    padding: 8px 12px;
    font-size: var(--os-font-size-sm);
    min-height: 32px;
  }

  &--medium {
    padding: 12px 16px;
    font-size: var(--os-font-size-base);
    min-height: 40px;
  }

  &--large {
    padding: 16px 20px;
    font-size: var(--os-font-size-lg);
    min-height: 48px;
  }

  &--error {
    border-color: var(--os-color-error-500);
    background-color: var(--os-color-error-50);
    box-shadow: 0 0 0 1px var(--os-color-error-500);
  }

  &--disabled {
    background-color: var(--os-color-neutral-100);
    color: var(--os-color-neutral-500);
    cursor: not-allowed;
    opacity: 0.5;
  }

  &--readonly {
    background-color: var(--os-color-neutral-50);
    cursor: default;
  }

  &:hover:not(:disabled):not(:readonly) {
    border-color: var(--os-color-primary-400);
    cursor: pointer;
  }

  &:focus {
    border-color: var(--os-color-primary-500);
    box-shadow: 0 0 0 2px var(--os-color-primary-100);
    transform: scale(1.02);
  }

  &:active:not(:disabled):not(:readonly) {
    transform: scale(0.98);
  }

  &::placeholder {
    color: var(--os-color-neutral-500);
    opacity: 1;
  }

  &:disabled::placeholder {
    color: var(--os-color-neutral-400);
  }
}

.os-input__prefix-icon,
.os-input__suffix-icon {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  color: var(--os-color-neutral-500);
  font-size: var(--os-font-size-lg);
  pointer-events: none;
  z-index: 1;
  transition: color 200ms ease-in-out;

  &--small {
    font-size: var(--os-font-size-base);
  }

  &--medium {
    font-size: var(--os-font-size-lg);
  }

  &--large {
    font-size: var(--os-font-size-xl);
  }
}

.os-input__prefix-icon {
  left: var(--os-spacing-sm);
}

.os-input__suffix-icon {
  right: var(--os-spacing-sm);
}

.os-input__clear {
  position: absolute;
  right: var(--os-spacing-sm);
  top: 50%;
  transform: translateY(-50%);
  background: none;
  border: none;
  color: var(--os-color-neutral-500);
  cursor: pointer;
  font-size: var(--os-font-size-lg);
  line-height: 1;
  padding: var(--os-spacing-xs);
  border-radius: var(--os-border-radius-sm);
  transition: all 200ms ease-in-out;
  z-index: 2;
  min-width: 44px;
  min-height: 44px;
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background-color: var(--os-color-neutral-100);
    color: var(--os-color-neutral-700);
  }

  &:focus {
    outline: 2px solid var(--os-color-primary-500);
    outline-offset: 2px;
  }

  &:active {
    transform: translateY(-50%) scale(0.95);
  }
}

.os-input__helper {
  font-size: var(--os-font-size-sm);
  color: var(--os-color-neutral-600);
  margin-top: var(--os-spacing-xs);
  line-height: 1.5;

  &--error {
    color: var(--os-color-error-500);
    font-weight: var(--os-font-weight-medium);
  }
}

// Responsividade Mobile-First
@media (max-width: 575px) {
  .os-input {
    &--small {
      min-height: 44px;
      padding: 12px 16px;
    }

    &--medium {
      min-height: 44px;
      padding: 12px 16px;
    }

    &--large {
      min-height: 48px;
      padding: 14px 18px;
    }
  }

  .os-input__clear {
    min-width: 44px;
    min-height: 44px;
  }
}

@media (min-width: 576px) and (max-width: 991px) {
  .os-input {
    &:hover:not(:disabled):not(:readonly) {
      border-color: var(--os-color-primary-400);
    }
  }
}

@media (min-width: 992px) {
  .os-input {
    &:hover:not(:disabled):not(:readonly) {
      border-color: var(--os-color-primary-400);
    }
  }
}

// Dark Mode Support
[data-theme='dark'] {
  .os-input {
    background-color: var(--os-color-surface);
    border-color: var(--os-color-neutral-600);
    color: var(--os-color-on-surface);

    &::placeholder {
      color: var(--os-color-neutral-400);
    }

    &:focus {
      border-color: var(--os-color-primary-400);
      box-shadow: 0 0 0 2px var(--os-color-primary-900);
    }

    &--disabled {
      background-color: var(--os-color-neutral-800);
      color: var(--os-color-neutral-500);
    }

    &--readonly {
      background-color: var(--os-color-neutral-800);
    }

    &--error {
      border-color: var(--os-color-error-500);
      background-color: var(--os-color-error-900);
    }
  }

  .os-input__helper {
    color: var(--os-color-neutral-400);

    &--error {
      color: var(--os-color-error-400);
    }
  }

  .os-input__prefix-icon,
  .os-input__suffix-icon {
    color: var(--os-color-neutral-400);
  }

  .os-input__clear {
    color: var(--os-color-neutral-400);

    &:hover {
      background-color: var(--os-color-neutral-700);
      color: var(--os-color-neutral-200);
    }
  }
}

// High Contrast Mode Support
@media (prefers-contrast: high) {
  .os-input {
    border-width: 2px;

    &:focus {
      border-width: 3px;
    }
  }

  .os-input__clear {
    border: 1px solid currentColor;
  }
}

// Reduced Motion Support
@media (prefers-reduced-motion: reduce) {
  .os-input,
  .os-input__clear,
  .os-input__prefix-icon,
  .os-input__suffix-icon {
    transition: none;
  }

  .os-input:focus {
    transform: none;
  }

  .os-input:active {
    transform: none;
  }

  .os-input__clear:active {
    transform: translateY(-50%);
  }
}

