    }

    &--warning {
      color: var(--os-color-warning-400);
    }

    &--error {
      color: var(--os-color-error-400);
    }

    &--info {
      color: var(--os-color-info-400);
    }

    &--disabled {
      color: var(--os-color-text-disabled-dark);
    }

    &--required::after {
      color: var(--os-color-error-400);
    }
  }
}

// High contrast mode support
@media (prefers-contrast: high) {
  .os-label {
    &--focused {
      outline-width: 3px;
    }

    &:focus-visible {
      outline-width: 3px;
    }
  }
}

// Reduced motion support
@media (prefers-reduced-motion: reduce) {
  .os-label {
    &--animated {
      transition: none;
    }

    &:hover:not(&--disabled) {
      transform: none;
    }

    &--hovered:not(&--disabled) {
      transform: none;
    }
  }
}

