computed(() => {
    const classes = ['os-navigation-item__button'];

    if (this.variant() !== 'default') {
      classes.push(`os-navigation-item__button--${this.variant()}`);
    }

    if (this.size() !== 'medium') {
      classes.push(`os-navigation-item__button--${this.size()}`);
    }

    return classes.join(' ');
