import {
  Component,
  input,
  output,
  computed,
  model,
  ChangeDetectionStrategy,
  forwardRef,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { MatRadioModule } from '@angular/material/radio';

export type OsRadioSize = 'small' | 'medium' | 'large';
export type OsRadioVariant = 'default' | 'primary' | 'secondary' | 'success' | 'warning' | 'error';
export type OsRadioRole = 'radio' | 'switch';

@Component({
  selector: 'os-radio',
  standalone: true,
  imports: [CommonModule, MatRadioModule],
  template: `
    <mat-radio-button
      [id]="inputId"
      [name]="name()"
      [value]="value()"
      [checked]="checked()"
      [disabled]="disabled()"
      [color]="matColor()"
      [class]="radioClass()"
      (change)="handleChange($event)"
      (blur)="handleBlur($event)"
      (focus)="handleFocus($event)"
      [attr.aria-describedby]="ariaDescribedBy() || null"
      [attr.aria-label]="ariaLabel() || null"
      [attr.aria-labelledby]="ariaLabelledBy() || null"
      [attr.aria-required]="required()"
      [attr.role]="radioRole()"
      [attr.data-animated]="animated()"
      [attr.data-size]="size()"
      [attr.data-variant]="variant()"
    >
      @if (label()) {
      {{ label() }}
      }
    </mat-radio-button>
  `,
  styleUrls: ['./os-radio.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => OsRadioComponent),
      multi: true,
    },
  ],
})
export class OsRadioComponent implements ControlValueAccessor {
  size = input<OsRadioSize>('medium');
  variant = input<OsRadioVariant>('default');
  name = input<string>('');
  value = model<string>('');
  label = input<string>('');
  checked = input(false);
  disabled = model(false);
  required = input(false);
  animated = input(true);
  role = input<OsRadioRole>('radio');
  ariaDescribedBy = input<string>('');
  ariaLabel = input<string>('');
  ariaLabelledBy = input<string>('');

  radioChange = output<string>();
  radioBlurEvent = output<FocusEvent>();
  radioFocusEvent = output<FocusEvent>();

  protected inputId = `os-radio-${Math.random().toString(36).substr(2, 9)}`;
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  private onChange = (value: string) => {
    // This will be set by registerOnChange
  };
  private onTouched = () => {
    // This will be set by registerOnTouched
  };

  // Mapeamento interno para Material
  protected matColor = computed(() => {
    switch (this.variant()) {
      case 'primary':
        return 'primary';
      case 'secondary':
        return 'accent';
      case 'success':
        return 'primary';
      case 'warning':
        return 'warn';
      case 'error':
        return 'warn';
      default:
        return undefined;
    }
  });

  radioClass = computed(() => {
    return [
      'os-radio',
      `os-radio--${this.size()}`,
      `os-radio--${this.variant()}`,
      this.disabled() ? 'os-radio--disabled' : '',
      this.checked() ? 'os-radio--checked' : '',
      this.animated() ? 'os-radio--animated' : '',
    ]
      .filter(Boolean)
      .join(' ');
  });

  radioRole = computed(() => {
    return this.role();
  });

  handleChange(event: { value: string }): void {
    const value = event.value;

    this.onChange(value);
    this.radioChange.emit(value);
  }

  handleBlur(event: FocusEvent): void {
    this.onTouched();
    this.radioBlurEvent.emit(event);
  }

  handleFocus(event: FocusEvent): void {
    this.radioFocusEvent.emit(event);
  }

  writeValue(value: string): void {
    // Update the model signal when FormControl value changes programmatically
    if (value !== this.value()) {
      this.value.set(value);
    }
  }

  registerOnChange(fn: (value: string) => void): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    // Update the disabled state when called by Angular Forms
    this.disabled.set(isDisabled);
  }
}

