      }

      &.os-radio__circle--secondary {
        border-color: var(--os-color-secondary);
      }

      &.os-radio__circle--success {
        border-color: var(--os-color-success);
      }

      &.os-radio__circle--warning {
        border-color: var(--os-color-warning);
      }

      &.os-radio__circle--error {
        border-color: var(--os-color-error);
      }
    }

    &--disabled {
      background-color: var(--os-color-surface-disabled);
      border-color: var(--os-color-outline-disabled);
      cursor: not-allowed;
    }
  }

  &__dot {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background-color: var(--os-color-primary);
    transition: all var(--os-transition-fast) ease;
    transform: scale(0);
    opacity: 0;

    .os-radio__circle--primary & {
      background-color: var(--os-color-primary);
    }

    .os-radio__circle--secondary & {
      background-color: var(--os-color-secondary);
    }

    .os-radio__circle--success & {
      background-color: var(--os-color-success);
    }

    .os-radio__circle--warning & {
      background-color: var(--os-color-warning);
    }

    .os-radio__circle--error & {
      background-color: var(--os-color-error);
    }

    .os-radio--small & {
      width: 10px;
      height: 10px;
    }

    .os-radio--large & {
      width: 16px;
      height: 16px;
    }

    .os-radio--checked & {
      transform: scale(1);
      opacity: 1;
    }
  }

  &__label-text {
    font-size: var(--os-font-size-base);
    font-weight: var(--os-font-weight-regular);
    color: var(--os-color-on-surface);
    line-height: var(--os-line-height-normal);

    .os-radio--small & {
      font-size: var(--os-font-size-sm);
    }

    .os-radio--large & {
      font-size: var(--os-font-size-lg);
    }

    .os-radio--disabled & {
      color: var(--os-color-on-surface-disabled);
    }
  }

  &:hover:not(&--disabled) {
    .os-radio__circle:not(.os-radio__circle--checked) {
      border-color: var(--os-color-primary);
      background-color: var(--os-color-primary-container);
      transform: scale(1.02);
    }
  }

  &:focus-within {
    .os-radio__circle {
      outline: var(--os-focus-ring-width) solid var(--os-focus-ring-color);
      outline-offset: 2px;
    }
  }

  // Active effects
  &:active:not(&--disabled) {
    .os-radio__circle {
      transform: scale(0.98);
    }
  }

  // Animaes de seleo
  &--animated {
    .os-radio__dot {
      animation: os-radio-select var(--os-transition-fast) ease-out;
    }
  }
}

// Keyframes para animaes
@keyframes os-radio-select {
  0% {
    transform: scale(0);
    opacity: 0;
  }
  50% {
    transform: scale(1.2);
    opacity: 0.8;
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}

// Suporte para prefers-reduced-motion
@media (prefers-reduced-motion: reduce) {
  .os-radio {
    &--animated {
      transition: none;
    }

    .os-radio__circle {
      transition: none;
    }

    .os-radio__dot {
      transition: none;
    }
  }

  @keyframes os-radio-select {
    0%,
    100% {
      transform: scale(1);
      opacity: 1;
    }
  }
}

