@use '../../theme/tokens' as tokens;

.os-select-container {
  display: flex;
  flex-direction: column;
  gap: var(--os-spacing-xs);
  width: 100%;
  position: relative;

  &--small {
    gap: var(--os-spacing-xxs);
  }

  &--large {
    gap: var(--os-spacing-sm);
  }

  &--error {
    .os-select__form-field {
      --mdc-outlined-text-field-error-outline-color: var(--os-color-error-500);
      --mdc-outlined-text-field-error-label-color: var(--os-color-error-500);
    }
  }

  &--disabled {
    opacity: 0.6;
    pointer-events: none;
  }

  &--focused {
    .os-select__form-field {
      --mdc-outlined-text-field-focus-outline-color: var(--os-color-primary-500);
    }
  }

  &--hovered:not(&--disabled) {
    .os-select__form-field {
      --mdc-outlined-text-field-hover-outline-color: var(--os-color-primary-400);
    }
  }

  &--opened {
    .os-select__form-field {
      --mdc-outlined-text-field-focus-outline-color: var(--os-color-primary-500);
    }
  }

  &--animated {
    .os-select__form-field {
      transition: all var(--os-transition-duration-standard) var(--os-transition-easing-standard);
    }
  }
}

// Material form field customization
.os-select__form-field {
  width: 100%;

  &--small {
    .mat-mdc-form-field {
      font-size: var(--os-font-size-sm);
      min-height: 44px; // Touch target minimum
    }
  }

  &--medium {
    .mat-mdc-form-field {
      min-height: 44px; // Touch target minimum
    }
  }

  &--large {
    .mat-mdc-form-field {
      font-size: var(--os-font-size-lg);
      min-height: 48px; // Larger touch target
    }
  }

  &--error {
    .mat-mdc-form-field {
      --mdc-outlined-text-field-error-outline-color: var(--os-color-error-500);
      --mdc-outlined-text-field-error-label-color: var(--os-color-error-500);
    }
  }

  &--disabled {
    .mat-mdc-form-field {
      opacity: 0.6;
    }
  }
}

.os-select__label {
  font-size: var(--os-font-size-sm);
  font-weight: var(--os-font-weight-medium);
  color: var(--os-color-text-primary);
  margin-bottom: var(--os-spacing-xs);

  &--required {
    &::after {
      content: ' *';
      color: var(--os-color-error-500);
    }
  }
}

.os-select__wrapper {
  position: relative;
  display: flex;
  align-items: center;
  border: 1px solid var(--os-color-border-default);
  border-radius: var(--os-border-radius-md);
  background-color: var(--os-color-background-primary);
  transition: all 0.2s ease;

  &:hover {
    border-color: var(--os-color-primary-400);
  }

  &:focus-within {
    border-color: var(--os-color-primary-500);
    box-shadow: 0 0 0 2px var(--os-color-primary-100);
  }

  &--disabled {
    background-color: var(--os-color-background-disabled);
    border-color: var(--os-color-border-disabled);
    cursor: not-allowed;
  }
}

.os-select {
  flex: 1;
  border: none;
  outline: none;
  background: transparent;
  font-family: var(--os-font-family-primary);
  color: var(--os-color-text-primary);
  cursor: pointer;
  appearance: none;
  transition: all 0.2s ease;

  &--small {
    padding: var(--os-spacing-sm) var(--os-spacing-md);
    padding-right: var(--os-spacing-xl);
    font-size: var(--os-font-size-sm);
    min-height: 44px; // Touch target minimum
  }

  &--medium {
    padding: var(--os-spacing-md) var(--os-spacing-lg);
    padding-right: var(--os-spacing-xl);
    font-size: var(--os-font-size-base);
    min-height: 44px; // Touch target minimum
  }

  &--large {
    padding: var(--os-spacing-lg) var(--os-spacing-xl);
    padding-right: var(--os-spacing-xl);
    font-size: var(--os-font-size-lg);
    min-height: 48px; // Larger touch target
  }

  &--error {
    color: var(--os-color-error-700);
  }

  &--disabled {
    color: var(--os-color-text-disabled);
    cursor: not-allowed;
  }

  option {
    padding: var(--os-spacing-sm);
    background-color: var(--os-color-background-primary);
    color: var(--os-color-text-primary);

    &:disabled {
      color: var(--os-color-text-disabled);
      background-color: var(--os-color-background-disabled);
    }
  }
}

.os-select__arrow {
  position: absolute;
  right: var(--os-spacing-sm);
  top: 50%;
  transform: translateY(-50%);
  pointer-events: none;
  color: var(--os-color-text-secondary);
  font-size: var(--os-font-size-xs);
  transition: transform 0.2s ease;

  .os-select:focus + & {
    transform: translateY(-50%) rotate(180deg);
  }
}

.os-select__helper {
  font-size: var(--os-font-size-xs);
  color: var(--os-color-text-secondary);
  margin-top: var(--os-spacing-xs);

  &--error {
    color: var(--os-color-error-600);
  }
}

.os-select__required {
  color: var(--os-color-error-500);
  font-weight: var(--os-font-weight-bold);
}

// Responsive design
@media (max-width: 575px) {
  .os-select-container {
    gap: var(--os-spacing-sm);
  }

  .os-select__form-field {
    &--small,
    &--medium,
    &--large {
      .mat-mdc-form-field {
        min-height: 48px; // Larger touch target on mobile
      }
    }
  }

  .os-select {
    &--small {
      min-height: 48px;
      padding: var(--os-spacing-md) var(--os-spacing-lg);
    }

    &--medium {
      min-height: 48px;
      padding: var(--os-spacing-lg) var(--os-spacing-xl);
    }

    &--large {
      min-height: 52px;
      padding: var(--os-spacing-xl) var(--os-spacing-xxl);
    }
  }
}

// Dark mode support
@media (prefers-color-scheme: dark) {
  .os-select__wrapper {
    background-color: var(--os-color-background-primary-dark);
    border-color: var(--os-color-border-dark);

    &:hover {
      border-color: var(--os-color-primary-400);
    }

    &:focus-within {
      border-color: var(--os-color-primary-500);
      box-shadow: 0 0 0 2px var(--os-color-primary-100-dark);
    }

    &--disabled {
      background-color: var(--os-color-background-disabled-dark);
      border-color: var(--os-color-border-disabled-dark);
    }
  }

  .os-select {
    color: var(--os-color-text-primary-dark);

    &--error {
      color: var(--os-color-error-400);
    }

    &--disabled {
      color: var(--os-color-text-disabled-dark);
    }

    option {
      background-color: var(--os-color-background-primary-dark);
      color: var(--os-color-text-primary-dark);

      &:disabled {
        color: var(--os-color-text-disabled-dark);
        background-color: var(--os-color-background-disabled-dark);
      }
    }
  }

  .os-select__label {
    color: var(--os-color-text-primary-dark);
  }

  .os-select__helper {
    color: var(--os-color-text-secondary-dark);

    &--error {
      color: var(--os-color-error-400);
    }
  }
}

// High contrast mode support
@media (prefers-contrast: high) {
  .os-select__wrapper {
    border-width: 2px;

    &:focus-within {
      outline: 3px solid var(--os-color-primary-500);
      outline-offset: 2px;
    }
  }
}

// Reduced motion support
@media (prefers-reduced-motion: reduce) {
  .os-select-container--animated {
    .os-select__form-field {
      transition: none;
    }
  }

  .os-select {
    transition: none;
  }

  .os-select__arrow {
    transition: none;
  }
}

