      font-size: var(--os-font-size-sm);
    }
  }

  &--medium {
    min-height: var(--os-touch-target-min);

    .os-toggle__input {
      min-height: var(--os-touch-target-min);
      min-width: var(--os-touch-target-min);
    }

    .os-toggle__label-text {
      font-size: var(--os-font-size-base);
    }
  }

  &--large {
    min-height: 48px;

    .os-toggle__input {
      min-height: 48px;
      min-width: 48px;
    }

    .os-toggle__label-text {
      font-size: var(--os-font-size-lg);
    }
  }

  // Variants com cores semnticas
  &--primary {
    &.os-toggle--checked {
      .os-toggle__input {
        --mdc-switch-selected-track-color: var(--os-color-primary-500);
        --mdc-switch-selected-handle-color: var(--os-color-white);
      }
    }
  }

  &--secondary {
    &.os-toggle--checked {
      .os-toggle__input {
        --mdc-switch-selected-track-color: var(--os-color-secondary-500);
        --mdc-switch-selected-handle-color: var(--os-color-white);
      }
    }
  }

  &--success {
    &.os-toggle--checked {
      .os-toggle__input {
        --mdc-switch-selected-track-color: var(--os-color-success-500);
        --mdc-switch-selected-handle-color: var(--os-color-white);
      }
    }
  }

  &--warning {
    &.os-toggle--checked {
      .os-toggle__input {
        --mdc-switch-selected-track-color: var(--os-color-warning-500);
        --mdc-switch-selected-handle-color: var(--os-color-white);
      }
    }
  }

  &--danger {
    &.os-toggle--checked {
      .os-toggle__input {
        --mdc-switch-selected-track-color: var(--os-color-danger-500);
        --mdc-switch-selected-handle-color: var(--os-color-white);
      }
    }
  }

  // Estados visuais
  &--checked {
    .os-toggle__label-text {
      color: var(--os-color-text-primary);
      font-weight: var(--os-font-weight-semibold);
    }
  }

  &--disabled {
    cursor: not-allowed;
    opacity: 0.6;

    .os-toggle__label-text {
      cursor: not-allowed;
      color: var(--os-color-text-disabled);
    }

    .os-toggle__description {
      color: var(--os-color-text-disabled);
    }

    .os-toggle__input {
      cursor: not-allowed;
    }
  }

  // Micro-interactions
  &:hover:not(.os-toggle--disabled) {
    background-color: var(--os-color-background-hover);
    transform: translateY(-1px);
    box-shadow: var(--os-shadow-sm);
  }

  &:active:not(.os-toggle--disabled) {
    transform: translateY(0);
    box-shadow: var(--os-shadow-xs);
  }

  // Focus ring com tokens
  &:focus-within {
    outline: var(--os-focus-ring-width) solid var(--os-focus-ring-color);
    outline-offset: var(--os-focus-ring-offset);
  }

  // Animaes
  &--animated {
    .os-toggle__input {
      transition: all var(--os-transition-duration-fast) var(--os-transition-easing-standard);
    }

    .os-toggle__label-text {
      transition: all var(--os-transition-duration-fast) var(--os-transition-easing-standard);
    }

    &:hover:not(.os-toggle--disabled) {
      transition: all var(--os-transition-duration-fast) var(--os-transition-easing-standard);
    }
  }

  // Suporte a reduced motion
  @media (prefers-reduced-motion: reduce) {
    &--animated {
      .os-toggle__input,
      .os-toggle__label-text {
        transition: none;
      }

      &:hover:not(.os-toggle--disabled) {
        transition: none;
        transform: none;
      }
    }
  }

  // Dark mode support
  @media (prefers-color-scheme: dark) {
    .os-toggle__label-text {
      color: var(--os-color-text-primary-dark, var(--os-color-text-primary));
    }

    .os-toggle__description {
      color: var(--os-color-text-secondary-dark, var(--os-color-text-secondary));
    }

    &--disabled {
      .os-toggle__label-text,
      .os-toggle__description {
        color: var(--os-color-text-disabled-dark, var(--os-color-text-disabled));
      }
    }
  }

  // High contrast mode
  @media (prefers-contrast: high) {
    .os-toggle__label-text {
      font-weight: var(--os-font-weight-bold);
    }

    &:focus-within {
      outline-width: 3px;
    }
  }
}

