(req, next) => {
  const notificationService = inject(NotificationService);

  return next(req).pipe(
    catchError((error: HttpErrorResponse) => {
      let errorMessage = 'Ocorreu um erro inesperado';
      let errorTitle = 'Erro';

      if (error.error instanceof ErrorEvent) {
        errorMessage = `Erro de cliente: ${error.error.message}`;
        errorTitle = 'Erro de Rede';
      } else {
        switch (error.status) {
          case 400:
            errorMessage = 'Dados invlidos. Verifique as informaes enviadas.';
            errorTitle = 'Dados Invlidos';
            break;
          case 401:
            errorMessage = 'No autorizado. Faa login novamente.';
            errorTitle = 'No Autorizado';
            break;
          case 403:
            errorMessage = 'Acesso negado. Voc no tem permisso para esta ao.';
            errorTitle = 'Acesso Negado';
            break;
          case 404:
            errorMessage = 'Recurso no encontrado.';
            errorTitle = 'No Encontrado';
            break;
          case 409:
            errorMessage = 'Conflito. O recurso j existe ou est em uso.';
            errorTitle = 'Conflito';
            break;
          case 422:
            errorMessage = 'Dados invlidos. Verifique os campos obrigatrios.';
            errorTitle = 'Validao';
            break;
          case 429:
            errorMessage = 'Muitas tentativas. Tente novamente em alguns minutos.';
            errorTitle = 'Muitas Tentativas';
            break;
          case 500:
            errorMessage = 'Erro interno do servidor. Tente novamente mais tarde.';
            errorTitle = 'Erro do Servidor';
            break;
          case 502:
            errorMessage = 'Servidor temporariamente indisponvel.';
            errorTitle = 'Servidor Indisponvel';
            break;
          case 503:
            errorMessage = 'Servio temporariamente indisponvel.';
            errorTitle = 'Servio Indisponvel';
            break;
          case 504:
            errorMessage = 'Timeout da requisio. Tente novamente.';
            errorTitle = 'Timeout';
            break;
          default:
            errorMessage = `Erro ${error.status}: ${error.message}`;
            errorTitle = 'Erro';
        }
      }

      console.error('HTTP Error:', {
        status: error.status,
        message: errorMessage,
        url: req.url,
        method: req.method,
      });

      notificationService.showError(errorMessage, errorTitle);

      return throwError(() => new Error(errorMessage));
    })
  );
}

