src/app/core/interceptors/auth/auth.inte.defaultValue:
(
  req: HttpRequest<unknown>,
  next: HttpHandlerFn
) => {
  const authService = inject(AuthService);

  if (shouldSkipAuth(req.url)) {
    return next(req);
  }

  return from(authService.getToken()).pipe(
    switchMap((token) => {
      if (!token) {
        return next(req);
      }

      const authReq = req.clone({
        setHeaders: {
          Authorization: `Bearer ${token}`,
        },
      });

      return next(authReq);
    }),
    catchError((error) => {
      if (error.status === 401) {
        authService.signOut();
        return throwError(() => error);
      }
      return throwError(() => error);
    })
  );
}

